INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('d2cc9fbe-24e3-48c1-9cad-13d0c7ab1654', '821610ae-4ec8-11e8-a4cf-985fd331b2ee', 'en', 0, 'Silvrettagletscher is a small temperate mountain glacier located in the north-eastern part of Switzerland in the Silvretta massif at the border to Austria. The present surface area is 2.7 km2, extending from 3090 m a.s.l. down to 2470 m a.s.l. First mass balance measurements date back to the 1910s. Seasonal observations at two stakes were conducted until 1959, when the stake network was increased to about 40 stakes.');
