INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('2278711a-f61c-4b79-b257-12dfebfe54ae', '81e98270-4ec8-11e8-86b3-985fd331b2ee', 'en', 0, 'Vadret dal Murtèl is situated in the inner-alpine Upper Engadine of south-eastern Switzerland. The east-facing cirque glacier next to Piz Corvatsch (3451 m a.s.l.) covers 0.3 km2 and is still remarkably crevassed in its steeper middle part. Exhibiting only very little debris cover along the foot of steep headwalls confining the glacier to the north and west, Vadret dal Murtèl is a typical clean-ice glacier.');
