INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('074d36e4-3230-4473-80fa-9b2a9bfc3b26', '81722f8f-4ec8-11e8-8842-985fd331b2ee', 'de', 0, 'Messungen der Schnee- und Firnakkumulation sowie der Schmelze und des Niederschlags im Akkumulationsgebiet des Claridenfirns werden seit 1914 von verschiedenen Forschenden durchgeführt. Die traditionelle glaziologische Methode wurde angewendet, indem ein Schneeschacht hinunter zur "Ockerschicht" des vorherigen Herbstes gegraben und das Wasseräquivalent gemessen wurde. Die spezifischen Jahresbilanzen werden seit 1957 jeden Herbst und regelmässig im Frühjahr an zwei hochgelegenenen Standorten auf einer Höhe von 2700 und 2900 m ü. M. ermittelt.');
INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('d608507b-a402-4c02-af49-fa6fa6687615', '81722f8f-4ec8-11e8-8842-985fd331b2ee', 'en', 0, 'Measurements of the snow and firn accumulation and melt, as well as of precipitation values in the accumulation area of the Claridenfirn, have been undertaken by various researchers since 1914. The traditional glaciological method was applied by digging a snowpit down to the layer of ochre applied the previous autumn and measuring the water equivalents. Specific annual balances were determined every autumn since 1957 and also regularly in spring at two plateau locations at altitudes of 2700 and 2900 m a.s.l.');
