INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('fdd3b876-c5ba-4514-a437-0a865f18a095', '813e9c70-4ec8-11e8-bda0-985fd331b2ee', 'de', 0, 'Der Sankt Annafirn ist ein nach Norden ausgerichteter, sehr kleiner Kargletscher in den Zentralschweizer Alpen. Der Gletscher ist durch steile Felswände geschützt, die das Sankt Annahorn (2937 m ü. M.) mit dem Chastelhorn (2973 m ü. M.) verbinden. Der Gletscher erstreckt sich über eine Fläche von 0.2 km2. Seit 2013 werden auch Messungen am nahe gelegenen Schwarzbachfirn durchgeführt. Von 1973 bis zum Jahr 2010 schrumpfte der Sankt Annafirn auf die Hälfte seiner ursprünglichen Fläche und verlor seit 1986 etwa zwei Drittel seines Volumens. Im Jahr 2013 betrug die gemessene maximale Gletscherdicke 42 m, die mittlere Gletscherdicke lag bei 16 m. Gemäss einem mittleren Klimaszenario wird der Sankt Annafirn in den nächsten 25 Jahren ein anhaltender und rasches Schrumpfen verzeichnen: Nur ein winziger Eisfleck in einer geschützten Nische wird voraussichtlich bis zur Mitte des 21. Jahrhunderts überleben.');
INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('caec1ca0-b980-4e7b-8c20-8a83a016e1b6', '813e9c70-4ec8-11e8-bda0-985fd331b2ee', 'en', 0, 'Sankt Annafirn is a north-facing very small cirque glacier in the central Swiss Alps protected by steep rockwalls connecting Sankt Annahorn (2937 m a.s.l.) with Chastelhorn (2973 m a.s.l.). The glacier covers an area of 0.2 km2. Glaciological investigations were started in 2012. Since 2013, measurements have been performed also on nearby Schwarzbachfirn. By 2010, Sankt Annafirn shrank to half of its initial surface area from 1973, and lost about two-thirds of its volume since 1986. Measured maximum glacier thickness reached 42 m and was 16 m on average in 2013. According to a medium climate scenario, Sankt Annafirn is expected to show ongoing and rapid shrinkage over the next 25 years. Only a tiny ice patch in a protected niche is predicted to survive until the middle of the 21st century.');
