INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('8d8d8c2e-9908-49c7-bd0e-0cdfc790717d', '80a394ee-4ec8-11e8-984c-985fd331b2ee', 'de', 0, 'Der Unteraargletscher ist der viertgrösste Talgletscher in den Berner Alpen mit einer Länge von 11.8 km und einer Fläche von 22.5 km2 (Daten von 2009). Hervorzuheben sind die grosse Mittelmoräne und die ausgedehnte Schuttbedeckung, die normalerweise 5 bis 15 cm dick ist. Die nach Osten ausgerichtete Zunge des Unteraargletschers besteht aus den beiden Hauptzuflüssen, dem Lauteraargletscher und dem Finsteraargletscher. Massenbilanzmessungen deuten darauf hin, dass die Gleichgewichtslinie des Unteraargletschers bei 2850 m ü. M. lag (2001). Das aktuelle Gletscherende, 1.5 km vom Grimselsee entfernt, liegt auf einer Höhe von 1930 m ü. M. (2009).');
INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('9b901db4-3307-4876-816d-49d3a6156388', '80a394ee-4ec8-11e8-984c-985fd331b2ee', 'en', 0, 'Unteraargletscher is the fourth largest valley glacier in the Bernese Alps, 11.8 km long and covering 22.5 km2 (data from 2009). A prominent feature is its large medial moraine and extensive debris cover, typically 5–15 cm thick. The tongue of the Unteraargletscher, oriented to the east, is formed by the two main tributaries, the Lauteraargletscher and the Finsteraargletscher. Mass-balance measurements indicate that the equilibrium-line altitude of Unteraargletscher was at 2850 m a.s.l. (2001). The present glacier terminus, 1.5 km from Lake Grimsel, is at an elevation of 1930 m a.s.l. (2009).');
