INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('4a610903-f60d-4e04-9f5c-abfcaee817da', '800b9aae-4ec8-11e8-a4bc-985fd331b2ee', 'en', 0, 'Findelengletscher (12.9 km2) and its former tributary Adlergletscher (2.0 km2) are located in the southern Valais in the Zermatt area. The two glaciers cover an elevation range from 2580 to 4120 m a.s.l. Findelengletscher is west-facing and is characterized by gently sloping high-elevation accumulation basins and a comparatively narrow glacier tongue. The region is relatively dry with equilibrium-line altitudes being among the highest in the Alps.');
