INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('4d9be142-5013-43ee-b8c5-ac92a35fe3bf', '7f5bd2ae-4ec8-11e8-b55b-985fd331b2ee', 'de', 0, 'Der Giétrogletscher im Kanton Wallis bedeckt eine Fläche von 5.17 km2, die Gletscherlänge beträgt 4.45 km (Daten von 2010). 1973 wies der Gletscher eine Fläche von 5.85 km2 auf. Die relative Flächenänderung beträgt somit -11.68 %. Spektakuläre Eisstürze bei Vorstössen im vom 16. und 19. Jahrhundert schütteten im Talgrund von Mauvoisin einen kegelförmigen Eisdamm auf. Ausbrüche des dahinter aufgestauten Sees verursachten 1595 und 1818 verheerende Flutwellen im Val de Bagnes. 1818 brach der Damm, nachdem der See durch einen unter der Leitung von Ignaz Venetz gebauten Eisstollen zu einem Drittel entleert war.');
INSERT INTO base_data.glacier_description (pk, fk_glacier, fk_language_type, fk_glacier_description_type, description) VALUES ('f4f1a6a8-cf2d-488e-b1fe-6369dbe36af9', '7f5bd2ae-4ec8-11e8-b55b-985fd331b2ee', 'en', 0, 'Glacier du Giétro is located in the canton of Valais. It covers an area of 5.17 km2 and its length is 4.45 km (data from 2010). In 1973, the glacier covered an area of 5.85 km2. The relative area change amounts to -11.68 %. Spectacular ice avalanches during glacier advances in the 16th and 19th centuries caused the formation of an ice dam in the valley floor of Mauvoisin. Outbreaks of the dammed lake behind caused 1595 and 1818 devastating floods in the Bagnes Valley. In 1818, the dam collapsed after the lake was emptied to one third by an ice tunnel built under the lead of Ignaz Venetz.');
