INSERT INTO glacier_hazard.event(pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES('a32280f9-fb8a-414b-9f41-58f97a6ce80d','7f6f33a1-4ec8-11e8-88b8-985fd331b2ee','2025','7','2','','','2025','7','7','','','','',NULL,NULL,'Die Entleerung begann sehr langsam am 2.7. Scheinbar wurde der Ausfluss des Lac des Faverges künstlich mit einem Bagger abgetieft. Das dürfte die Seeentleerung angestossen haben. Es ist aber nicht ganz klar wann und wo die Interaktion stattfand. Der See entleerte sich anschliessend bis am 5.7. mit moderatem Tempo und dann zügig bis er am 7.7. morgens leer war. Der Abfluss war zwischen dem 5.7. und dem 7.7. mittags deutlich erhöht. Es wurden aber keine Überschwemmung verzeichnet.','Geopraevent Portal, https://www.msn.com/de-ch/nachrichten/other/lenker-gletschersee-ist-ohne-komplikationen-ausgelaufen/ar-AA1I6A91?ocid=msedgntp&pc=ASTS&cvid=d0b68a30ecc24cf282966a1bb9e38cb2&ei=8','GLAMOS');
INSERT INTO glacier_hazard.event(pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES('513a4005-9579-4f5d-8f7a-523f3c05e05f','7ff18300-4ec8-11e8-9c90-985fd331b2ee','2025','5','28','15:24','','2025','5','28','','','9.5 mio','1400000', 1, 0,'Als Folge eines Bergsturzes am Kleinen Nesthorn am 19.05.2025 als grosse Teile der Flanke in mehrere Teilabstuerze auf den Gletscher hinunterstuerzten, beschleunigte die Eismasse bis zum vollstaendigen Kollaps der gesamten Gletscherzunge.  Dabei stuerzten rund 2.9 mio m3 Eis und 6.5 mio m3 Fels ins Tal hinunter, forderten ein Todesopfer und zerstoerten grosse Teile des evakuerten Dorfes Blatten. Die Zerstoerungen durch die Druckwelle mit Eis und Staubpartikel reichte rund 500m den Gegenhang hinauf bis zum Weiler Wiessenried. Die Ablagerung von rund 8 mio m3 erstreckte sich im Talgrund ueber eine Flaeche von 1.4 km2 und staute in der Folge den Fluss Lonza auf, der weitere Teile des Dorfes ueberflutete.','Ereignisanalyse VAW anhand Drohenenaufnahmen und historischen Luftaufnahmen der swisstopo','GLAMOS');
INSERT INTO glacier_hazard.event(pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES('800779d1-50cc-41b1-a87a-5b399df55a8b','81722f8f-4ec8-11e8-8842-985fd331b2ee','2025','6','14','','','','','','','','200000','',NULL,NULL,'Aus der Nordflanke des Grats zwischen Clariden und Bocktschingel hat sich am 14.6.2025 ein grösserer Felssturz ereignet.   Die Auslösung kommt aus der steilen Nordwand (ca. 2700 m.ü.M. und damit potentiell Permafrost, Kalk).  Da gibt es schon vorher Anzeichen von verstärkter Steinschlag-Aktivität.  Beim Sturz wurde sicher noch Schnee und auch Teile des Gletschers mitgerissen.  Die Ablagerung erstreckt sich bis auf den in der Karte namenlosen Gletscher Im Griess.  Die Verantwortlichen von der Abteilung Naturgefahren vom Kanton UR haben am 18.6.2025 Aufnahmen mit einer Drohne gemacht und die Sturzmasse auf etwa 200000 m3 geschätzt.','Naturgefahren vom Kanton UR Ereignis sichtbar auf den Sentinel-Bilder vom 14.6.2025','GLAMOS');
INSERT INTO glacier_hazard.event(pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES('b95a359c-0450-40f5-bf9b-29fdec0b0e61','7fdce98f-4ec8-11e8-9185-985fd331b2ee','2025','7','27','','','','','','','','','',NULL,NULL,'Ausbruch einer Wassertasche an der steilen Eisfront auf etwa 3900m des Gletscherteils auf der NNW Flanke am Nordend.  Gemaess Mitteilungen von oesterreichische Touristen auf dem Gornergrat: "Nach einigen lauten Poltergeräuschen ergoss sich am Vormittag ein Wasserfall aus dem Gletscher. Auch nach 2 Stunden war der Austritt nicht beendet."','Fotos und Videos durch beobachtenden Touristen','GLAMOS');
INSERT INTO glacier_hazard.event(pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES('5e3b930e-6dc7-4a83-8e50-b30620beb168','813e9c70-4ec8-11e8-bda0-985fd331b2ee','2025','9','1','','','','','','','','20000','',NULL,NULL,'Die Ablagerungen von einem Felssturz aus der bergseitigen Flanke bedecken grosse Teile des St. Annafirns und das Vorfeld.','Medienmitteilung AFJ/UR: https://www.ur.ch/mmdirektionen/129382 das AFJ betreibt eine automatische Kamera welche die Entwicklung der Felsinstabilitaet dokumentierte','GLAMOS');
