INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('21f9fb5a-aaf8-4e8c-bb2d-6d3b61f64c6a','7fa4c291-4ec8-11e8-9945-985fd331b2ee','1895','09','11','04:45','','1895','09','11','05:15','','4000000','','6',null,'Eine Eismasse von ca. 4 Mio m³ stürzte auf die Spittelmatte. Sechs Menschen verloren ihr Leben, 169 Stück Vieh (davon 158 Stück Rindvieh, 9 Schweine, 1 Maultier, 1 Hund) wurden erschlagen. Vier Häuser wurden zerstört, ein drittel des Waldes und ein grosser Teil des Ertrages des Sommers an Käse, Butter und Zieger war vernichtet.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('b947a27b-b625-49b1-b999-7ffb1efdac9c','7fa4c291-4ec8-11e8-9945-985fd331b2ee','1782','08','18','','','1782','08','18','','','','','4',null,'Ein Gletscherabbruch erreichte die Spittelmatte. Vier Personen und gegen 100 Haustiere kamen ums Leben.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('bc9d8ded-8032-4db5-9a30-5cff881fa06e','81919e70-4ec8-11e8-a3dd-985fd331b2ee','1957','08','13','','','1957','08','13','','','','',null,null,'Ausbruch einer Wassertasche. Ausgehend vom Gebiet des Oberalpfirns und des Hängegletschers in der Westflanke des Oberalpstocks Hochwasser im Gletscherbach, das im Zusammenhang stand mit kurzen, aber sehr intensiven Niederschlägen im Gotthardgebiet (maximale Niederschlagsmengen bei Biasca 115.5 mm, im Gotthard Hospiz 89.7 mm). Die Erosionsrinne liegt direkt unterhalb des Gletschers, wobei das nördliche Bachbett zwischen 2480 und 2030 m ü. M. breit ausgehoben wurde. Die im Gletscher gestauten Wassermassen stammten wohl auch von den starken Niederschlägen in dem fraglichen Gebiet. Der Etzlibach hatte ebenfalls Hochwasser (Normalereignis), die sich dadruch ergebenden kumulierenden Abflüsse bewirkten im Maderanertal grosse Übermurungen und Überschwemmungen. Folgen: Übermurung von Kulturland mit grossen Schäden bei Bristen und bis Amsteg. Der Schaden auf Privatgebiet wurde auf rund 30000 CHF geschätzt, durch Zerstörung von Wehren, Brücken und Waldweg entstand der Öffentlichkeit allerdings ein weitaus grösserer Schaden. ','-','UZH');
