INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('bd295caf-e83b-4c2f-9a7d-d259aaf88729','800f4430-4ec8-11e8-972b-985fd331b2ee','1982','06','11','03:00','','1982','06','11','07:00','','','',null,null,'Ein Wassertaschenausbruch verursachte einen Murgang und staute die Saaser Vispa bis zum Strassenrand. Um 7:00 Uhr wurde ein starker, fontänenartiger Wasseraustritt auf der orographisch linken Gletscherseite beobachtet. Das Wasser war zu diesem Zeitpunkt nicht schmutzig. Der herumliegende Eisschutt wurde kaum erodiert. In der gestauten Vispa ereignete sich nach einem kurzen Stau ein sekundäres Hochwasser mit einer Abflussspitze, die beim Limnigraphen in Saas Balen um 2 m³/s grösser war als der Normalabfluss.','-','VAW 2003');
