INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8dcd5427-fb4c-43f7-8596-a62749a8cca9','817f27e1-4ec8-11e8-8991-985fd331b2ee','1983','','','','','1983','','','','','','',null,null,'Wegen der häufigen Eisabbrüche waren die Zugänge zur Wasserfassung und von dieser zur Fridolinshütte nur unter grosser Gefahr begehbar. Der Gletscher ist über die Wasserfassung 2 hinaus in die Schlucht vorgestossen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('18be7d77-aa0c-407b-9e97-9a541b791047','7ff18300-4ec8-11e8-9c90-985fd331b2ee','2008','03','22','07:00','','2008','03','22','','','','',null,null,'Nach heftigem Schneefall ging eine kombinierte Eis-Schnee Lawine auf die Strasse zwischen Ried und Blatten nieder. Ein Abschnitt von 30 Metern wurde mit einer mehr als ein Meter dicken Schnee- und Eisschicht bedeckt. Ansonsten gab es keine Schäden.','Following heavy snowfall, a combined ice/snow avalanche reached the road between Ried and Blatten and covered it along a 30 m stretch, with more than 1 m of ice/snow. Luckily no damage occurred.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('23eff8ea-dddf-4ba0-8ac8-699728415320','7ff18300-4ec8-11e8-9c90-985fd331b2ee','1993','12','18','04:00','','1993','12','18','','','20000','',null,null,'Eine kombinierte Eis-Schneelawine löste sich gegen 4:00 Uhr, drang bis zum Bachufer der Lonza vor und verschüttete die Langlaufloipe. Das Ausbruchsvolumen betrug ca. 20000 m³ .','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a795cba7-73c3-4307-a1fb-2d3642b011d7','7ff18300-4ec8-11e8-9c90-985fd331b2ee','1999','12','29','20:45','','1999','12','29','','','1000','',null,null,'Um 20:45 Uhr drang eine Staublawine bis zur Kantonsstrasse vor und verschüttete sie auf einer Länge von ca. 40 m. In der bis zu 2 m dicken Scheeablagerung auf der Strasse wurden kopfgrosse Steine gefunden. Die Strasse war zu dieser Zeit wegen Lawinengefahr noch geschlossen, die Schneeräumungsarbeiten waren jedoch im Gang. Sehr wahrscheinlich hat ein kleiner Eisabbruch (weniger als 1000 m³) vom Birchgletscher diese Lawine ausgelöst. Schäden waren keine zu verzeichnen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('059532d6-f003-404e-9d45-f750deda1656','7f9a3b40-4ec8-11e8-9c5e-985fd331b2ee','2013','02','21','','','2013','02','21','','','250000','',null,null,'Ein Teil des Hängegletschers unterhalb des Weisshorngipfels brach ab (250000 m³ Eis) und stürzte als Eislawine ins Tal. Das Dorf Randa wurde mit Pulverschnee überzogen, es ereignete sich aber kein Schaden.','Part of the hanging glacier below the Weisshorn peak broke off (250000 m³ ice) and the avalanche moved down as far as the valley bottom. The village of Randa was covered by powder snow. No damage occurred.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('18c7c762-ff49-475c-94ed-1d5565ce74f1','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','2007','02','28','10:40','','2007','02','28','21:50','','','',null,null,'Um 10:40 Uhr stürzte eine Lawine von der Ostflanke des Weisshorns  auf "Sennjini" in der Nähe von Randa. Um 21:50 Uhr erreichte eine weitere Lawine den Talboden. Es entstand kein Schaden.','An avalanche originating at the eastern face of Weisshorn came down on ”Sennjini” near Randa at 10:40 am. At 9:50 pm another avalanche reached the valley bottom. No damage occurred.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('1d08862f-2491-4230-8bc1-21b620e13396','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','1867','01','15','','','1867','01','15 ','','','','',null,null,'Eine kombinierte Eis-Schneelawine staute die Vispa ohne Schadenfolge.','-','VAW 2003');
