INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('156ffb50-2aef-40f9-b58a-b3e0c0d01d46','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','1946','01','02','','','1946','01','02 ','','','','',null,null,'Ein Eissturz vom Bisgletscher staute vorübergehend die Vispa. Die Strasse und die BVZ-Bahnlinie wurden verschüttet. Die Eisablagerungen blieben während einem ganzen Jahr im Tal liegen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8683ee6d-5ea6-4a3f-a0bc-f7eb76a4f9ae','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','1918','12','25','','','1918','12','25 ','','','','',null,null,'Vom Bisgletscher löste sich eine kombinierte Eis-Schneelawine. Die Ablagerungen stauten die Vispa und der See reichte bis zum Bahnhofbuffet.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8e8f71e5-a6cf-4763-824c-0dd4c098fda0','7f9a3b40-4ec8-11e8-9c5e-985fd331b2ee','2005','03','31','','','2005','03','31','','','400000','',null,null,'Ein weiterer Teil des Hängegletschers unterhalb des Ostgrates des Weisshorns brach ab. Zusammen mit dem vorhergehenden Event brachen insgesamt 460000 m³ Eis ab. Kein Schaden.','Part of the hanging glacier located immediately below the eastern ridge broke off in two events with a total ice volume of 460000 m³. A real-time monitoring system was installed for early warning purposes.No damage occurred.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('930b3e6d-d478-48a2-8654-d3b4aad5aa22','7f9a3b40-4ec8-11e8-9c5e-985fd331b2ee','1980','02','05','','','1980','02','05 ','','','500000','',null,null,'Am Weisshorn löste sich eine Lawine in einer Höhe von 4200 - 4300 m ü. M. Die Strasse und die BVZ-Bahnlinie wurden verschüttet, mehrere Gebäude zerstört und der 15 m hohe Lawinenkegel staute die Vispa. Das Seevolumen von 500000 m³ entleerte sich ohne Ausbildung einer Flutwelle.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('aaaee269-0377-4a46-99ea-789e9210559a','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','1986','02','04','23:50','','1986','02','04 ','','','','',null,null,'Eine Eislawine verschüttete die Strasse und das BVZ-Bahngleise auf einer Länge von 200 m.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('ad22a595-b643-42ed-ac4b-ae20e5da7ff1','7f9a3b40-4ec8-11e8-9c5e-985fd331b2ee','1819','12','27','','','1819','12','27 ','','','13000000','','2',null,'Bei einer kombinierten Eis-Schneelawine donnerte ein Volumen von 13 Mio m³ zu Tal. Es wird heute vermutet, dass sich dieser Gletschersturz aus der Flanke des N-Grates löste. Der Luftdruck des Staubanteils zerstörte 113 Gebäude und forderte zwei Menschenleben. Die Ablagerungen stauten einen See auf, der sich ohne Ausbildung ener Flutwelle entleerte.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('ae33b543-489d-43fa-a657-35fee97d4f97','7f9a3b40-4ec8-11e8-9c5e-985fd331b2ee','2005','03','23','','','2005','03','24','','','120000','',null,null,'Ein Teil des Hängegletschers unterhalb des Ostgrates des Weisshorns brach ab. Kein Schaden.','Part of the hanging glacier located immediately below the eastern ridge broke off in two events with a total ice volume of 460000 m³. A real-time monitoring system was installed for early warning purposes. No damage occurred.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('bafd75d7-6645-481e-9140-0cc20430ec3d','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','1720','02','18','','','1720','02','18 ','','','','','12',null,'Eine kombinierte Eis-Schneelawine forderte 12 Tote in Randa.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('c46581b7-3145-4b11-9e1c-1271e681f13f','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','1999','03','','','Ende','1999','03','','','Ende','','',null,null,'Es löste sich eine kombinierte Eis-Schneelawine mit einer Auslaufstrecke von ca. 3.5 km. Die Ablagerungen erreichten die Vispa ohne diese zu stauen und bedeckten verschiedene Wanderwege.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('c5029a45-a190-4e15-8cf6-6f1123218ac1','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','1828','03','07','','','1828','03','07 ','','','','',null,null,'Eine Schneelawine (eventuell infolge eines Eissturzes) richtete in der Gegend von Äderna grosse Schäden an.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('c7408436-8544-44ec-96af-d504b6022a4e','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','2012','12','13','','','2012','12','13','','','','',null,null,'Ein Gletscherabbruch von dem steilen Teil des Bisgletschers (3300 m ü. M.) verursachte eine Eislawine, die fast bis zum Talboden (1450 - 1480 m ü. M.) reichte. Teile des Dorfs Randa wurden mit Pulverschnee bedeckt. Es entstand kein Schaden.','A glacier breakoff from the steep part of Bisgletscher (3300 m a.s.l.) caused an ice avalanche reaching down to an elevation of between 1450 and 1480 m a.s.l., immediately above the valley bottom. Powder snow extended down to parts of the village of Randa. No damage occurred.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('c8dbadc5-151c-4378-88cb-dd3ceb1688d0','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','2019','12','18','','','2019','12','18','','','','50000',null,null,'Es ereigneten sich zwei kleinere Eisabbrüche mit je ca. 50000 m³ am Hängegletscher unter dem Weisshorn. Sie kamen im flachen Bereich des Bisgletschers zum Stillstand und verursachten keine Schäden. Eine Anlage zur Früherkennung grösserer Ereignisse ist installiert.','-','Matthias Huss');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('db7696c7-7fc9-48cc-a51a-04f8593270d8','7f9a3b40-4ec8-11e8-9c5e-985fd331b2ee','1973','08','19','','','1973','08','19 ','','','130000','',null,null,'Grosser Eissturz (ca. 130000 m³) vom Hängegletscher an der Nordostflanke des Weisshorns.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f6849bfa-58ef-4e74-ab56-d3b1b8a43929','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','1858','01','31','','','1858','01','31 ','','','','',null,null,'Infolge eines Eissturzes vom Bisgletscher wurde die Vispa gestaut. Dieses Ereignis war ohne Schadenfolge.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f75a8ce6-c705-49fe-bc6e-7bd104b05672','7fbb09b0-4ec8-11e8-a935-985fd331b2ee','2012','01','05','','','2012','01','05','','','','',null,null,'Ein Eissturz von dem steilen Teil des Bisgletschers (ca. 3300 m ü. M.) löste eine grosse kombinierte Eis-/Schneelawine aus. Die Strasse und Eisenbahnlinie nach Zermatt wurden getroffen. Es entstand Schaden an Bäumen, Gebäuden und Stromleitungen. Ausserdem wurden zwei Autos und ein Lastwagen (mit Personen darinnen) von der Lawine verlagert.','An icefall from the steep part of Bisgletscher (approx. 3300 m a.s.l.) triggered a large combined ice/snow avalanche which reached the road and railway leading to Zermatt. Damage was incurred to trees, buildings and electricity conduits. Two cars and a truck (with persons inside) were displaced by the avalanche.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8959d648-747e-4607-a58d-c8fb313e8ed2','7fd45e0f-4ec8-11e8-b1d3-985fd331b2ee','2004','08','06','','','2004','08','06','','','130000','',null,null,'Eissturz von 130000 m³; kein Schaden.','Icefall of 130000 m³ of ice; no damage.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('92894ec5-01f0-4959-adea-63deb0f7ee0a','7fd45e0f-4ec8-11e8-b1d3-985fd331b2ee','2004','08','26','','','2004','08','26','','','130000','',null,null,'Eissturz von 130000 m³; kein Schaden.','Icefall of 130000 m³ of ice; no damage.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('cc777b2c-64da-44d5-9570-c2824ab7fc3f','7fd45e0f-4ec8-11e8-b1d3-985fd331b2ee','2004','08','18','','','2004','08','18','','','130000','',null,null,'Eissturz von 130000 m³; kein Schaden.','Icefall of 130000 m³ of ice; no damage.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('856ffb50-2aef-40f9-b58a-b3e0c0d01d46','805b685e-4ec8-11e8-a23e-985fd331b2ee','1994','09','24','','','1994','09','24','','','','',null,null,'Murgang aus dem Vorfeld des Bodmergletschers. Während starken Niederschlägen wurde eine historische Moräne im Gletschervorfeld erodiert, was in einer Bresche von 10-20 m Tiefe resultierte. Der darauffolgende Murgang ging in mehreren Teilschüben talwärts durch den Lauigraben und mündete in den Hauptalfluss (Chrummbach) knapp unterhalb des Dorfes Simplon. Die Schuttmassen, die den Chrummbach erreichten, verursachten schwere Schäden in der darunterliegenden Ortschaft Gabi, wo einige Leute evakuiert werden mussten. Ebenso wurde die Brücke der Kantonsstrasse bei Simplon zerstört. Die Brücke über den Bergbach des Lauigrabens wurde geschützt durch seitliche Dämme, die bei hoher Schuttführung einen Überlauf erlauben (keine Verklausung bei der Brücke).','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('9d6c877e-a90a-4075-9e4a-67439040d19f','80253d30-4ec8-11e8-a760-985fd331b2ee','1974','06','22','','','1974','06','22','','','','','2',null,'Zwei französische Touristen wurden auf dem Weg von Trachsellauenen zur Schmadrihütte im Bereich "Breitlauenen" von einem Eisabbruch überrascht und getötet.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('cd97474f-c7a5-49fd-b531-68be1f0fca3e','80253d30-4ec8-11e8-a760-985fd331b2ee','2004','08','04','','','2004','08','04','','','','',null,null,'Beim Hubelgletscher (NW-Flanke der Ebnenfluh) im oberen Einzugsgebiet des Roten Bachs staute sich Wasser, welches später ausbrach und einen Murgang bildete. Das Wasser mobilisierte sehr viel Geschiebe, welches vom Roten Bach in die Weisse Lütschine transportiert wurde. Ein Teil des Materials wurde zwischen Lochweid und Trachsellauenen deponiert, während der Rest erst in Stechelberg abgelagert wurde. Einige Autos wurden mitgerissen, eingeklemmt oder eingeschottert.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('e93fc861-640f-42e0-ad74-c73f0b181493','815cabc0-4ec8-11e8-b3b0-985fd331b2ee','1942','','','','','1942','','','','','','',null,null,'Aufgrund von sichtbaren Spuren muss in diesem Jahr ein Wasserausbruch oder ein sonstiges mit dem Gletscher in Verbindung zu bringendes Hochwasser stattgefunden haben. Zudem wurde 1927 auf demselben Gletscher die Bildung von zwei kleinen temporären Seen beobachtet. Keine Schadenmeldungen.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('16942c81-86cf-4552-9600-ca881850b5f2','805dd95e-4ec8-11e8-a0e0-985fd331b2ee','1992','08','11','','','1992','08','11','','','','','3',null,'Ein Eisabbruch am Challifirn tötete ein deutsches Ehepaar und dessen Sohn, die sich auf dem Weg zur Mittellegihütte befanden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a9f6e46f-2118-41a1-92d8-54693ec69f24','805dd95e-4ec8-11e8-a0e0-985fd331b2ee','1994','05','02','','','1994','05','02','','','','',null,null,'Ein Eisabbruch am Challiifirn löste eine grosse kombinierte Eis-Schneelawine aus. Die Lawine erreichte sogar das 200 Meter hangaufwärts gelegene Bergrestaurant "Bäregg".','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('fde4d887-b536-427a-bb31-1963c025ce06','805dd95e-4ec8-11e8-a0e0-985fd331b2ee','1983','09','03','','','1983','09','09','','','','',null,null,'Grössere Eisstürze vom Challifirn auf das Zungenende und in die Gletscherschlucht stauten die Lütschine. Durch die Flutwelle bei Ausbruch des gestauten Wassers sind Eistrümmer in grosser Menge murgangähnlich mitgeführt und hinter dem Stauwehr Burglauenen angeschwemmt worden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('e0336f49-a6af-46bb-8381-33e6a80a93dc','8071d68f-4ec8-11e8-9aa4-985fd331b2ee','1993','09','','','','1993','09','','','','','',null,null,'Gletscherhochwasser mit Murgang in Taferna.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('715cfea3-1267-48f8-ab23-891a145684e7','80ffbeb0-4ec8-11e8-abc0-985fd331b2ee','1987','08','24','','','1987','08','24','','','70000','',null,null,'Murgang aus dem Vorfeld des Chüebodengletschers (Gerental, VS) im Zusammenhang mit den 1987er Unwettern. Das Anrissgebiet liegt auf 2360 m (60 %, resp. 31° Neigung), es handelt sich dabei um eine Moränenhalde mit aufgesetzten Stirnmoränen mit Sturzschutt und glazialen Schlammstromablagerungen mit einer Mächtigkeit von 25-45 m (im Anrissgebiet Permafrost). Die Moränenhalde weist eine deutliche Schichung auf. Die 1850er Vergetscherung reichte bis auf 2300 m. Das Pauschalgefälle beträgt für die Hauptablagerungen 43 % (23°, bis auf 2100 m), für Teilschübe 24 % (14°, bis auf 1780 m). Total wurden 70000 m³ Material erodiert und 55000 m³ akkumuliert. Als Folge kam es zu Überführungen in Oberwald, wobei nicht klar ist, wieviel jeder der einzelnen Murgänge aus dem gesamten Gerental dazu beitrug.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f7134227-c930-4309-932e-90254911495d','8176754f-4ec8-11e8-9a29-985fd331b2ee','2019','06','16','','Nacht','2019','06','17','','Abend','','',null,null,'Das Wasser des Claridensees wurde durch Schnee aufgestaut. Nach dem Bruch des Schneedamms kam es zu stark erhöhtem Abfluss in der Nacht bis 15 Uhr. Keine Schäden.','-','Matthias Huss');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8451cd26-76ab-4ba8-a2ad-d8ad2c223238','809711cf-4ec8-11e8-9106-985fd331b2ee','1884','','','','','1884','','','','','','',null,null,'Die auf dem Punkt 2882 vom Blatt 397 des Siegfriedatlas gelegene Dossenhütte wurde 1879 gebaut. 1884 musste sie in Folge des schnellen Zuwachs des Dossengrat-SE Gletschers abgebaut werden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('03c9e711-1123-4acb-9735-323c737d3a89','804cc25e-4ec8-11e8-a91d-985fd331b2ee','2012','01','28','','','2012','01','28','','','','',null,null,'Ein Eisabbruch löste eine Eis-/Schneelawine aus. Eine Mischung aus Eis und Pulverschnee drangen durch das Fenster in die Kabine eines Pistenfahrzeuges auf der Black Rock Skipiste ein. Die Fenster (inklusive Rahmen) des obersten Gebäudes am Eigergletscher wurden durch den Druck der Lawine eingedrückt. Die Terrassentische wurden gegen die Wand geschleudert, manche flogen sogar über das Geländer. Noch nie in den vergangenen 30 Jahren war eine Eislawine so weit nach unten gedrungen.','Ice rupture triggered a ice/snow avalanche. A mixture of ice and powder snow invaded the cabin of a ski groomer through an open window at the Black Rock ski run. The windows (including the frames) of the uppermost building at Eigergletscher were pushed in by the pressure. The tables on the terrace were flattened against the wall, some of them flew over the rail. Never in the past 30 years has an ice avalanche come this far down.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('6624cf77-78b8-4e81-9c99-f2d9d2849ee6','804cc25e-4ec8-11e8-a91d-985fd331b2ee','1990','08','20','','','1990','08','20','','','','',null,null,'Gletscherabbruch und grosse Eislawine.','Glacier breakoff event and major ice avalanche.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('d8d93c41-2e3f-424f-b649-16734bf1ac9a','804cc25e-4ec8-11e8-a91d-985fd331b2ee','1982','05','','','','1982','05','','','','','',null,null,'Eine Eislawine wurde südöstlich der Station Eigergletscher der Jungfraubahn durch eine historische Moräne in zwei Ströme geteilt. Der kleinere Strom floss nur 60 m (horizontal) südlich und 25 Höhenmeter unterhalb der Stationsgebäude vorbei.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('6ef1f2d6-3b02-47b0-bd20-d948ebf296ef','800b2580-4ec8-11e8-80e9-985fd331b2ee','1981','','','','','1981','','','','','','',null,null,'Eine Eis-Schneelawine zerstörte die Skiliftanlage im "Leeboden".','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('adf279f8-c675-4387-ba0a-01d709148c1e','800b2580-4ec8-11e8-80e9-985fd331b2ee','1919','','','','','1919','','','','','','',null,null,'Eislawinen am Fallgletscher verschütteten den Weg zur Mischabelhütte.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('1e90ffc3-d50a-4b53-bbbc-d7b63efbbd71','8005ce4f-4ec8-11e8-8fd1-985fd331b2ee','2009','09','15','','','2009','09','20','','','300000','',null,null,'Mehrere Eisstürze von der Zunge des Feegletschers. Insgesamt brachen 300000 m³ Eis in verschiedenen Ereignissen ab. Die Eislawinen vom 15., 19. und 20. September reichten bis an den Wanderweg. Es enstanden kein Schäden.','Several icefalls from the tongue of Feegletscher. In total, 300’000 m³ of ice broke off in the course of several events. The ice avalanche resulting from three events (on the 15th, 19th and 20th September 2009) extended as far down as a hiking trail, but fortunately no damage or injuries occurred.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('2745e203-575c-4d26-a7de-bf17ad0dd2af','8005ce4f-4ec8-11e8-8fd1-985fd331b2ee','1916','','','','','1917','','','','','','',null,null,'Aufgrund eines kräftigen Gletschervorstosses musste Jungwald notgeschlagen werden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('9bc716aa-a6e8-4bf3-b6a2-ac526aa76064','8005ce4f-4ec8-11e8-8fd1-985fd331b2ee','1994','01','04','15:30','','1994','01','04','','','','',null,'12','Eine Lawine löste sich von der Ostftanke des Doms. Diese Lawine, bespickt mit winzigen Eispartikeln, bewirkte den ähnlichen Druckeffekt auf der Skipiste unterhalb Spielboden. Es wurden wieder 12 Skifahrer erfasst.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a340ca7b-5cb8-423e-a27b-c9eb14134a7a','7fff8cc0-4ec8-11e8-af69-985fd331b2ee','1997','07','','','','1997','08','','','','4000','',null,null,'Ein Eissturz löste sich vom Hängegletscher 1500 m östlich vom Alphubel (Kante). Ein kleiner Teil (ca. 4000 m³) glitt auf feuchtem Firn (volumenunabhängig!) doppelt so weit wie die Hauptmasse. Die Sturzbahn war unberechenbar und gefährdete eine Skiliftanlage.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('b081bc93-9a04-4363-ab1e-ca42e33203f7','8005ce4f-4ec8-11e8-8fd1-985fd331b2ee','2017','05','30','10:30','','2017','05','31','23:30','','300000','',null,null,'Plötzlicher Ausbruch von mindestens 300000 m³ Wasser aus einer subglazialen Wassertasche.','Sudden release of at least 300’000 m³ of water from a subglacial water pocket.','Glaciological Report  No. 137/138');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('dbdf30d5-b256-480f-b160-dc516751386f','8005ce4f-4ec8-11e8-8fd1-985fd331b2ee','2017','12','17','11:40','','2017','12','18','16:45','','24000','',null,null,'Ausbruch von 24000 m³ Wasser aus einer subglazialen Wassertasche.','Outflow of 24’000 m³ of water from a subglacial water pocket.','Glaciological Report  No. 137/138');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('e05b44e2-4005-4973-ac69-323ab2158aae','8005ce4f-4ec8-11e8-8fd1-985fd331b2ee','1994','01','02','14:30','','1994','01','02','','','','',null,'12','Eine Eisstaublawine brach an der Ostftanke zwischen Dom und Lenzspitze bei ca. 3400-3500 m los und löste eine derartige Druckwelle aus, dass 12 Personen, die sich auf der Skipiste zwischen Spielboden und Punkt 2345.1 bei Gletscheralp befanden, auf die Seite gestossen und verletzt wurden.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('ebf4d1c9-8faa-4b5a-bf1d-b4c4e6f09ba6','822ea1c0-4ec8-11e8-ad20-985fd331b2ee','1816','','','','','1816','','','','','','',null,null,'Eisblöcke verschütteten 28 Schafe.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('fd069bbe-f03e-42bb-9d16-c145e915536e','7fff8cc0-4ec8-11e8-af69-985fd331b2ee','2003','','','','','2003','','','','','','',null,null,'Im Sommer 2003 hat sich eine grosse Zungenrutschung östlich der Seilbahnstation Längflue ereignet.','-','WGMS');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('2b646438-ed0d-4c69-842d-6962617e1852','7ff7c48f-4ec8-11e8-9cfb-985fd331b2ee','1967','02','','','','1967','02','','','','80000 - 100000','',null,null,'Ein Wassertaschenausbruch mit einem grossen Volumen ereignete sich im Winterhalbjahr. Während etwa drei Tagen wurde von der Grande Dixence SA ein erhöhter Wasserabfluss registriert. Die Abllussspitze betrug 2 - 3 m³/s und das Ausbruchvolumen ca. 80000 bis 100000 m³. Dieses Ereignis hatte keine Schadenfolgen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('5c13746d-8b2f-40aa-9656-7dd9fac789e2','7ff7c48f-4ec8-11e8-9cfb-985fd331b2ee','1937','09','03','','','1937','09','04','','','','',null,null,'Eislawine durch den Abbruch eines steilen Hängegletschers in der Nacht vom 3. auf den 4. September; eventuell auf eine Zungenrutschung zurückzuführen.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('7768fb23-4edf-43f2-ab79-c9d557800b8b','7ff7c48f-4ec8-11e8-9cfb-985fd331b2ee','1899','08','01','','','1899','08','01','','','','',null,null,'Ein Wassertaschenausbruch verursachte einen Murgang. Dabei wurden die Eisenbahn- und die Telegrafenlinie beschädigt.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('7dba6baa-bf47-44e2-94f4-64bcdf91af3b','7ff7c48f-4ec8-11e8-9cfb-985fd331b2ee','1945','','','','ca.','1945','','','','ca.','','',null,null,'Ein Gletschersturz vom Festigletscher erreichte den Talboden. Die Eistrümmer im Talboden schmolzen während des ganzen Sommers nicht ab.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f3e4ea86-7b67-487f-a87f-c6f1c9dde4b1','7ff7c48f-4ec8-11e8-9cfb-985fd331b2ee','1976','08','22','','','1976','08','22','','','','','3','5','Am 22.8.1976 ereignete sich eine weitere Eislawine auf dem Festigletscher. Eine Gruppe von 10 Bergsteigern wurde von den abbrechenden Eismassen erfasst. 3 Personen wurden getötet, 2 schwer und 3 leicht verletzt. Die Eislawine ging knapp am Dorf Randa vorbei, Schaden wurde nicht gemeldet.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('4acd5653-7c8e-4d13-83ba-638885cf026c','800b9aae-4ec8-11e8-a4bc-985fd331b2ee','1982','05','30','','','1982','05','30','','','','',null,null,'Der Ausbruch einer Wassertasche führte zu einem Hochwasser, das den Damm einer Wasserfassung durchbrach und eine Baustelle überflutete.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a34979e2-041c-4081-a6b7-5375cc1b953c','800b9aae-4ec8-11e8-a4bc-985fd331b2ee','1979','','','','','1980','','','','','','',null,null,'Es fand ein Gletschervorstoss in Richtung einer grossen Wasserfassung der Grande Dixence SA statt. Die Wasserfassung musste aus Sicherheitsgründen umgebaut werden. Der Vorstoss erreichte das Bauwerk bis auf wenige Meter, überfuhr es jedoch nicht.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('e64b0dbe-3624-404e-aef5-3aaec7bf03ae','800b9aae-4ec8-11e8-a4bc-985fd331b2ee','1943','07','23','','','1943','07','23','','','','',null,null,'Während eines Gletscherrückzugs ereignete sich ein Wasserausbruch mit Eisblocktransport.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('993d736b-fbeb-449d-9f17-500db2a64401','8053ee51-4ec8-11e8-a34d-985fd331b2ee','1998','10','','','','1998','10','','','','','',null,null,'Vom Fletscherhorngletscher sind mehrmals Zungenrutschungen mit grösseren Eisabbrüchen beobachtet worden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('73856d43-3885-46c6-9725-3d139c8cf03e','7fa7338f-4ec8-11e8-ac48-985fd331b2ee','2019','07','24','','Abend','2019','07','24','','','','',null,null,'Der Ausbruch (wahrscheinlich) einer Wassertasche am Gabelhorngletscher verursachte ein Hochwasser im Triftbach, das zu Schäden in Zermatt führte. Das Frühwarnsystem für Hochwasser funktionierte.','-','Matthias Huss');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a9a3f9cf-1561-4ea4-ae1f-40fee531522e','80905b0f-4ec8-11e8-8e80-985fd331b2ee','1829','','','','','1829','','','','','','',null,null,'1829 gab der Gauligletscher Trümmer einer alten Hütte frei, die wohl auf der Gaulialp gestanden war und von der man sagt, dass sie vor langer Zeit bewohnt gewesen sei.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('57e97449-1811-45de-a0bf-e5e2122654f8','7feb8f91-4ec8-11e8-a985-985fd331b2ee','2005','08','25','','','2005','08','26','','','1100000','',null,null,'Grösserer Eisabbruch oberhalb des "Canalone Imseng" am südöstlichen Rand des Ghiacciaio del Monte Rosa in der Nacht vom 25/26 August 2005. Auf dem Weg erodierte die Eislawine weiteres Eis und Fels, wodurch sich ihr Volumen vergrösserte und 1.1 Mio Kubikmeter Material in der Vertiefung des zu jenem Zeitpunkt kleinen supraglazialen Sees "Lago Effimero" ablagerte.  Aufgrund des tiefen Wasserstandes des Lago Effimero entstand keine bedrohliche Flutwelle. Die Vertiefung des ehemals grösseren Sees scheint für das frühe Stoppen der Eislawine die Hauptverantwortung zu tragen. Der Staubanteil der Eislawine dagegen überfuhr die Seitenmoräne des Ghiacciaio del Belvedere und bedeckte die ganze Ebene oberhalb des Rifugio Zamboni mit Eis- und Gesteinsbruchstücken und traf auch die Hütte, wo jedoch kein grosser Schaden entstand. Die partielle Auffüllung der Depression des ehemaligen Lago Effimero bewirkt zudem eine Reduktion des Retentionseffektes bei zukünftigen Lawinenereignissen. Es entstanden keine Personenschäden.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('87db9493-7094-4597-a530-816b01faf2d4','7feb8f91-4ec8-11e8-a985-985fd331b2ee','2003','06','18','','','2003','06','18','','','2500000','',null,null,'Subglazialer Ausbruch von supraglazialem See auf dem Belvedere Gletscher. Der Lago Effimero bildete sich bereits erstmals im Herbst 2001 auf dem Belvedere Gletscher. Im Frühjahr 2002 hat er sich dann sehr schnell sehr stark vergrössert bis zu einem Volumen von ca. 3.5 Mio m³ Ende Juni 2002 (und war damit historisch der grösste supraglaziale See in den Alpen). Der See senkte sich dann durch Herausbilden von subglazialen Entwässerungskanälen und Installation von Pumpen durch die italienischen Behörden im Sommer 2002 langsam wieder ab. Im Frühjahr 2003 bildete sich der supraglaziale See erneut und vergrösserte sich stark, bis am 18. Juni und folgende Tage ein subglazialer Ausbruchs über die orographisch linke Zunge stattfand.  Als Folge ereignete sich ein starkes Hochwasser in der Anza. Der Abfluss der Anza nahm während des 18. Juni kontinuierlich zu und erreichte einen Spitzenwert von knapp 20 m3/s. In den darauffolgenden Tagen nahm der Abfluss kontinuierlich wieder ab. Es gab keine Schäden in Macugnaga oder entlang des Valle Anzasca, da das Flussbett bereits auf einen höheren Maximalabfluss ausgebaut worden war. ','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f0de8fae-88e4-4dfc-94c0-2b8469a225b2','8104528f-4ec8-11e8-a98c-985fd331b2ee','1987','08','24','','','1987','08','24','','','38000','',null,null,'Murgang aus dem Vorfeld des Rotondogletschers (Gerental, VS) im Zusammenhang mit den 1987er Unwettern. Der Murgang erfolgte um Mitternacht auf einer Anrisshöhe von 2480 m mit vier unabhängigen Anrissen. Es handelt sich dabei um einen glazifluvialen Schutthang (Moränenhalde) unterhalb der Karschwelle des Rotondogletschers mit subrezenter Wallmoräne mit einer gegen unten zunehmenden Mächtigkeit (einige bis mehrere 10 m). Die Fläche oberhalb des Anrisses wird mit 90 ha angegeben, davon 50 ha Eis und Schnee. Die Hangneigung des Anrissgebietes beträgt 53 % (28°), das Pauschalgefälle 35 % (19°) im Fall bis 2220 m oder 21 % (12°) im Fall bis 2130 m Auslauf. Die Niederschlagssumme war wie bei den anderen Murgängen im Gerental um 180 mm plus 30-35 mm aus der Schmelze. Total wurden 38000 m³ erodiert und 33000 m³ akkumuliert. Der Murgang floss über 0.7 km im Vorfluter weiter bei nur geringem Gefälle (12 %, resp. 7°). Schäden durch Überführungen in Oberwald. Es ist jedoch schwer festzustellen, welche Schäden durch diesen Murgang welche durch jene aus dem übrigen Gerental (ungefähr zeitgleiche Murgänge, Saasgletscher, Chüebodengletscher, Sidelengetscher), verursacht wurden.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('235bba3b-7a83-4108-a70a-f0c56e3b80b5','81120e2e-4ec8-11e8-875b-985fd331b2ee','1987','08','24','','','1987','08','25','','','30000 - 40000','',null,null,'Auslösung eines Murgangs aus dem Vorfeld des Ghiacciaio di Pesciora. Mindestens neun einzelne, unabhängige Anrissstellen in rezenten bis subrezenten Moränen wurden verzeichnet. Es wurden ca. 30000 bis 40000 m³ Geschiebe im Ticino abgelagert. Eine Brücke wurde zerstört, die Nufenenstrasse stark beschädigt.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('2f1c3557-f43c-4fb2-a802-d4251573dbe5','8036f070-4ec8-11e8-8bee-985fd331b2ee','1969','01','03','','','1969','01','03','','','5000 - 6000','',null,null,'Eisabbruch zwischen dem kleinen und grossen Silberhorn. Es entstand Waldschaden auf etwa 5000 und 6000 m³. lm Sandbach und im Trümmelbach wurden sämtliche Häuser eingedrückt und zahlreiche Bäume entwurzelt.','-','VAW 2003');
