INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('47a6d2c9-dc34-462e-9759-414e1430d272','7f589e61-4ec8-11e8-9be9-985fd331b2ee','1894','06','28','','','1939','08','07','','','1000000','',null,null,'Am damaligen Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma (2400 m ü. M.) bildete sich ein supraglazialer See. Ein Seevolumen von über 1 Mio m³ brach am 28. Juni 1894 aus. Das Hochwasser in der Dranse riss im Val de Bagnes 17 Brücken mit, zerstörte verschiedene Strassenabschnitte und wertvolles Kulturland.','-','VAW 2003');
