INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('7ca40efd-5156-4b90-ae85-039cd14043d5','7f589e61-4ec8-11e8-9be9-985fd331b2ee','1898','06','17','','','1898','06','17','','','','',null,null,'Seeausbruch am Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma. Neben beträchtlichen Schäden in den Ortschaften Champsec und Lourtier wurden 16 Brücken, verschiedene Sägewerke, Mühlen und andere Gebäude zerstört.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('b13d968b-af62-4f72-992d-6fe20a21757a','7f589e61-4ec8-11e8-9be9-985fd331b2ee','1926','','','','Sommer','1926','','','','Sommer','','',null,null,'Am damaligen Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma bildete sich erneut ein supraglazialer See. Dank der rechtzeitigen Herstellung eines Abflusstollens entleerte sich der See ohne Bildung einer Flutwelle.','-','VAW 2003');
