INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f2943469-b895-4266-b0df-0b2204d08ba3','7f589e61-4ec8-11e8-9be9-985fd331b2ee','1895','06','18','','','1895','06','18','','','500000','',null,null,'Seeausbruch am damaligen Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma mit einem Ausbruchvolumen von 500000 m³.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('0d27b3aa-d6ea-434f-94e9-59d8b412c90f','7f8071b0-4ec8-11e8-8a31-985fd331b2ee','1943','','','','','1952','','','','','','',null,null,'Es ereigneten sich immer wieder kleinere Hochwasser. Einbrüche am Gletschertor stauten jeweils einen subglazialen See. Verschiedene Sanierungsversuche (sprengen des Riegels) scheiterten.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('39b62a05-540f-40cb-9098-8fde496f0c9d','7f8071b0-4ec8-11e8-8a31-985fd331b2ee','1850','','','','','1850','','','','','','',null,null,'Der vorstossende Glacier de Ferpècle überfuhr Weideland und zerstörte die Chalets der Alp de Ferpècle.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('729b4580-fa4b-4217-9aff-058b818b2408','7f8071b0-4ec8-11e8-8a31-985fd331b2ee','1952','08','04','','','1952','08','04','','','250000','',null,null,'Die beiden Gletscher hatten sich zurückgezogen, bildeten jedoch immer noch eine gemeinsame Zunge. Das Gletschertor verlegte sich ans untere Ende des Felsriegels. Dahinter bildeten sich drei subglaziale Seen, welche durch einstürzendes Eis unterteilt und gestaut wurden. Am 4. August 1952 brachen 100 m oberhalb des Gletschertors die aufgestauten Wassermassen (mechanischer Bruch des Eisdamms) seitlich aus und bildeten ein grosses Hochwasser. Mit der Flutwelle wurde ein Wasservolumen von 255000 m³ evakuiert, der Abfluss in Evolène betrug 230 m³/s und die Wellenfront breitete sich mit 10-20 km/h aus. Selbst eine Brücke, die in 12 m Höhe über den Bach führte, wurde fortgerissen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a24e8ccd-e3f2-4e0c-b451-1ff3645d93c1','7f8071b0-4ec8-11e8-8a31-985fd331b2ee','1943','08','23','','','1943','08','23','','','1600000','',null,null,'Die Gletscherzungen der Glacier de Ferpecle und der Glacier du Mont Miné waren vereint. Auf der orographisch rechten Seite des Glacier de Ferpecle wurde 1942 ein Felsriegel freigelegt. Der Gletscher schob sich am Felsriegel auf, wodurch der Abfluss gehemmt wurde. Unterhalb des Riegels bildete sich in einer Depression ein subglazialer See. Durch eine plötzliche Verlegung des subglazialen Abflusses lief dieser See 1943 aus und verursachte ein zerstörerisches Hochwasser (1600000 m³).','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a6cf4134-348b-47b6-a756-4aef331548d4','7f8071b0-4ec8-11e8-8a31-985fd331b2ee','1817','','','','','1817','','','','','','',null,null,'Der vorstossende Glacier de Ferpècle überfuhr Weideland und zerstörte die Chalets der Alp de Ferpècle.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('c28a591f-3980-4a32-8772-f7ffdadd452a','7f8071b0-4ec8-11e8-8a31-985fd331b2ee','1868','','','','','1868','','','','','','',null,null,'Der Gletscher stösst in Weidegelände vor. Der Vorstoss von 1868 war bedeutend grösser als jener von 1817. Die Chalets von Ferpècle (unbekannten Baudatums) wurden zur Hälfte zerstört, die Weiden vom Gletscher überfahren. ','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('10370bda-1e77-463f-b7a0-7f013df54829','7f1309de-4ec8-11e8-b128-985fd331b2ee','1898','06','22','','','1898','06','22','','','','',null,null,'Durch den Ausbruch einer intraglazialen Wassermasse kam es zu Überschwemmungen, wobei der Wald von l’Ancône überflutet wurde. Das Hochwasser dauerte ungefähr eine Stunde. Es entstandengrosse Schäden im Wald von l’Ancône.','-','UZH');
