INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f81d11ed-0a38-4268-8def-84e9c168560b','7f9cd34f-4ec8-11e8-b0d8-985fd331b2ee','1834','08','27','','','1834','08','28','','','','',null,null,'Während starken Niederschlägen sammelte sich Wasser intraglazial an. Der Ausbruch der Wassermassen durch die zwei Seitenmoränen verursachte beträchtliche Schäden in Val d Anniviers. Das Hochwasser der Navizance riss verschiedene Brücken, Mühlen und Scheunen mit und setzte Teile des Dorfes Chippis unter Wasser. Ausserdem bildete sich ein temporärer See zwischen Chippis, Chalais und Grône.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('b04de6a4-af73-4964-92b3-0850afb1f6ae','7f5e91cf-4ec8-11e8-a2f5-985fd331b2ee','1983','09','','','','1983','09','','','','','',null,null,'Während einer Rutschung brach die 600 m breite Zunge des Glacier d Epicoune auf einer Länge von 300 - 400 m in zahllose, kleine Schollen auf und stürzte teilweise über die steile Felsstufe ins Gletschervorfeld ab. Die Abstürze ereigneten sich portionsweise aus der Ausbruchsnische auf 2800- 2900 m ü. M.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('7ec53c90-f4e9-483f-a6a9-eb7930d62031','7f0dd9c0-4ec8-11e8-b7d6-985fd331b2ee','1930','08','24','','','1922','08','02','','','','',null,null,'Ein Wassertaschenausbruch vom Glacier des Grands dauerte 15 Stunden, riss eine Bresche ins Moränenmaterial und löste einen Murgang aus.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('554797a4-d1a5-44af-a9a2-4a51bb103e6f','7f9de4c0-4ec8-11e8-9582-985fd331b2ee','1899','06','08','','','1899','06','08','','','','',null,null,'Seeausbruch am damaligen Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma. Dieses Ereignis blieb ohne Schadenfolgen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('b185543a-8c47-4498-ae79-303d32977b1e','7f9de4c0-4ec8-11e8-9582-985fd331b2ee','1894','06','28','','','1939','08','07','','','1000000','',null,null,'Am damaligen Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma (2400 m ü. M.) bildete sich ein supraglazialer See. Ein Seevolumen von über 1 Mio m³ brach am 28. Juni 1894 aus. Das Hochwasser in der Dranse riss im Val de Bagnes 17 Brücken mit, zerstörte verschiedene Strassenabschnitte und wertvolles Kulturland.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('bc9206e1-81c0-4fc3-962d-483d59e53b14','7f9de4c0-4ec8-11e8-9582-985fd331b2ee','1926','','','','Sommer','1926','','','','Sommer','','',null,null,'Am damaligen Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma bildete sich erneut ein supraglazialer See. Dank der rechtzeitigen Herstellung eines Abflusstollens entleerte sich der See ohne Bildung einer Flutwelle.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('ca4da5fa-f487-4c28-9d8b-12e1592417b9','7f9de4c0-4ec8-11e8-9582-985fd331b2ee','1895','06','18','','','1895','06','18','','','500000','',null,null,'Seeausbruch am damaligen Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma mit einem Ausbruchvolumen von 500000 m³.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('df4f71a1-ea33-46bf-87da-ccb5fa7142d8','7f9de4c0-4ec8-11e8-9582-985fd331b2ee','1898','06','17','','','1898','06','17','','','','',null,null,'Seeausbruch am Zusammenfluss des Glacier de Crête Seche und des Glacier dOtemma. Neben beträchtlichen Schäden in den Ortschaften Champsec und Lourtier wurden 16 Brücken, verschiedene Sägewerke, Mühlen und andere Gebäude zerstört.','-','VAW 2003');
