INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('1d33b305-ed77-40a6-a833-09a71533dd3e','7f166540-4ec8-11e8-8917-985fd331b2ee','1990','07','10','08:00','','1990','07','10','18:00','','30000','',null,null,'Um etwa 8 Uhr morgens brachen 30000 m³ Material aus der Moräne des Glacier du Dolent aus. Der anschliessende Murgang drang bis zum Weiler l A Neuve vor, wo ein Chalet beschädigt wurde. Dieses Ereignis dauerte bis am späteren Nachmittag.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('e7a177af-8c99-4a19-a746-4ea01a24a83b','7f166540-4ec8-11e8-8917-985fd331b2ee','1898','06','22','','','1898','06','22','','','','',null,null,'Eine Wassertasche soll sich aus dem Glacier du Dolent entleert haben und einen Murgang gebildet haben (keine Schäden).','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('5b6d7a54-ab4b-4de1-b97c-2e164c43374d','7f5bd2ae-4ec8-11e8-b55b-985fd331b2ee','1818','06','16','16:30','','1818','06','16','','','20000','','44',null,'lm Frühjahr 1818 fiel eine geringe Wasserführung der Dranse auf. Die Inspektion des Kantonsingenieurs lgnaz Venetz ergab, dass sich ein gewaltiger Eisdamm von ca. 10 Mio m³ Volumen gebildet hatte. Am 13. Juni 1818 betrug das vom Eis gestaute Seevolumen ca. 30 Mio m³. Am 16. Juni 1818 hatte der See eine Länge von 2 km, eine Breite von 200 m und war ca. 60 m tief. Täglich stieg der Pegel zwischen 0.3 und 1.6 m an. lgnaz Venetz begann am 11 . Mai mit dem Vortrieb eines Abflussstollens. Der Durchstich auf der Seeseite erfolgte am 4. Juni wenig oberhalb des Seespiegels. In den ersten drei Tagen funktionierte die Seeentleerung zufriedenstellend und das fliessende Wasser hatte die Stollensohle um ca. 10 m abgesenkt. Die thermische Erosion durch die Reibungswärme wurde immer ausgeprägter. Auf der Seeseite fand eine intensive Kalbung statt und die Eisberge wurden zum Überlauf getrieben. Auf der Talseite bildete sich infolge Rückwärtserosion ein Wasserfall. Dabei wurde der Kanal immer kürzer und gleichzeitig bildete sich ein subglazialer Abfluss mit Betterosion auf der Stirnseite des Eiskegels. Am 16. Juni 1818 um 16h30 kollabierte die Tunnelsohle in das subglaziale Abflusssystem und es brachen 20 Mio m³ Wasser aus. Die zerstörerische Flutwelle erreichte 1.5 Stunden später Martigny. Bei diesem Hochwasser verloren im Tal 10 Menschen und in Martigny 34 Menschen ihr Leben, da bei einem Fehlalarm die Warnfeuer Tage zu früh abgebrannt wurden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('5b7a6428-c66d-4e16-b25d-6ee6e17b7303','7f5bd2ae-4ec8-11e8-b55b-985fd331b2ee','1640','','','','','1640','','','','','','',null,null,'Ein weiteres Hochwasser wurde im Jahre 1640 verzeichnet. Weitere lnformationen liegen nicht vor.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('6578896e-16f9-4d28-9801-8f41cb48dc51','7f5bd2ae-4ec8-11e8-b55b-985fd331b2ee','1549','08','07','','','1549','08','07','','','','',null,null,'Ein Gletscherhochwasser richtete beträchtliche Schäden in Martigny an. Verschiedene Brücken wurden zerstört.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('b03ec673-1bf2-40b8-aa20-07b03cb71ed1','7f5bd2ae-4ec8-11e8-b55b-985fd331b2ee','1817','05','27','','','1817','05','27','','','','',null,null,'Ein kräftiger Vorstoss des Glacier du Giétro zwischen 1805 und 1818 war mit intensiver Eislawinentätigkeit verbunden. Der entstandene Eiskegel staute einen See, der am 27. Mai 1817 ausbrach. Zunächst lief das Wasser unter dem Eisdamm ab. Mit der zunehmenden Verfestigung wurde der Abfluss blockiert und es kam zu einer Seebildung. Am 27. Mai 1817 entleerte sich der See, das Hochwasser richtete keinen Schaden an.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f24d401f-8c79-4441-aeaf-1f2f59c4e580','7f5bd2ae-4ec8-11e8-b55b-985fd331b2ee','1595','05','25','','','1595','05','25','','','','','140',null,'Vermutlich entstand nach Gletschervorstössen mit einer intensiven Eislawinentätigkeit (Sturzhöhe 700 m) ein regenerierter Gletscher am Talboden des Glacier du Giétro. Der Eiskegel staute einen See, der am 25. Mai 1595 ausbrach. lm Val de Torrembey und im Val de Bagnes verloren bei diesem Ereignis ca. 140 Menschen das Leben und 500 Gebäude wurden zerstört.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('9ede05f7-269c-47bb-87c7-bd4fdf005386','7f3ab61e-4ec8-11e8-bc63-985fd331b2ee','1981','04','30','08:40','','1981','04','30','','','','','6',null,'Eislawine im ‘Korridor’ des Hängegletschers am Grand Combin. Die Aufstiegsroute zum Grand Combin führt durch eine ‘Korridor’ genannte Zone, die stark Eislawinen gefährdet ist. Die Eislawine löste sich um 8.40 Uhr und riss 6 Alpinisten 500 bis 600 m in die Tiefe und begrub sie. Alle starben.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('e15c8538-04b2-418f-a336-258eb8c3f64b','7f469d00-4ec8-11e8-91d0-985fd331b2ee','1988','','','','','1988','','','','','','',null,null,'Ein Murgang löste sich aus einem proglazialen See (Lac du Grand Désert). Die Überschwemmung reichte bis zur Wasserfassung von Cleuson Dixence.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('34f4228d-4756-4230-92ff-1f323a7a26f6','7f7acc61-4ec8-11e8-8917-985fd331b2ee','1952','08','04','','','1952','08','04','','','250000','',null,null,'Die beiden Gletscher hatten sich zurückgezogen, bildeten jedoch immer noch eine gemeinsame Zunge. Das Gletschertor verlegte sich ans untere Ende des Felsriegels. Dahinter bildeten sich drei subglaziale Seen, welche durch einstürzendes Eis unterteilt und gestaut wurden. Am 4. August 1952 brachen 100 m oberhalb des Gletschertors die aufgestauten Wassermassen (mechanischer Bruch des Eisdamms) seitlich aus und bildeten ein grosses Hochwasser. Mit der Flutwelle wurde ein Wasservolumen von 255000 m³ evakuiert, der Abfluss in Evolène betrug 230 m³/s und die Wellenfront breitete sich mit 10-20 km/h aus. Selbst eine Brücke, die in 12 m Höhe über den Bach führte, wurde fortgerissen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('63181f1c-36c0-4b4f-b351-209746987500','7f7acc61-4ec8-11e8-8917-985fd331b2ee','1943','','','','','1952','','','','','','',null,null,'Es ereigneten sich immer wieder kleinere Hochwasser. Einbrüche am Gletschertor stauten jeweils einen subglazialen See. Verschiedene Sanierungsversuche (sprengen des Riegels) scheiterten.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('d4c9c225-9fd5-4aa8-85a7-7d4ab0994a82','7f7acc61-4ec8-11e8-8917-985fd331b2ee','1943','08','23','','','1943','08','23','','','1600000','',null,null,'Die Gletscherzungen der Glacier de Ferpècle und der Glacier du Mont Miné waren vereint. Auf der orographisch rechten Seite des Glacier de Ferpecle wurde 1942 ein Felsriegel freigelegt. Der Gletscher schob sich am Felsriegel auf, wodurch der Abfluss gehemmt wurde. Unterhalb des Riegels bildete sich in einer Depression ein subglazialer See. Durch eine plötzliche Verlegung des subglazialen Abflusses lief dieser See 1943 aus und verursachte ein zerstörerisches Hochwasser (1600000 m³).','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('25365120-972d-485e-b3aa-5f0fce6309a3','7f0eeb30-4ec8-11e8-aab0-985fd331b2ee','1960','08','05','','','1960','08','06','','','840000','',null,null,'Ein 48-stündiger  Wassertaschenausbruch führte zu einem Hochwasserabfluss von 25 m³/sec. Bei diesem Ereignis wurden verschiedene Brücken und Strassenabschnitte weggespült.','-','VAW 2003');
