INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('717a1baa-81dc-48ae-8d36-563c3e4739e0','80471d0f-4ec8-11e8-892f-985fd331b2ee','1993','08','01','','','1993','08','01','','','150000','',null,null,'Ausbruch eines moränengedämmten Sees. Es handelte sich um den östlichen der beiden Sirwolteseen, der von einer Moränenbastion gestaut wurde. Während den starken Niederschlägen im Zusammenhang mit dem Briger Hochwasser brach der See über die Moräne aus und erodierte eine 10-20 m tiefe Bresche (18-20 mm Niederschlag pro Stunde, Volumen der Bresche: 150000 m³). Der dadurch entstandene Murgang erreichte gerade den Vorfluter des Haupttals (Chrummbach) am Simplonpass und in mehreren Schüben. In einzelnen flacheren Flächen wurde Material abgelagert. Das gesamte Alpgebiet auf dem Kegel wurde übermurt, eine Alphütte wurde dabei zerstört. Durch den stark erhöhten Sedimenttransport im Chrummbach entstanden an der Simplonpassstrasse Schäden.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('d7edbddf-0231-4bb4-93d4-7d3727535162','80471d0f-4ec8-11e8-892f-985fd331b2ee','1993','09','24','','','1993','09','24','','','300000','',null,null,'Der untere Sirwoltesee brach am 24. September 1993 aus und bildete im Moränenmaterial eine 200 m lange Bresche. Der Erosionsquerschnitt betrug 800 m² und 150000 m³ Lockermaterial wurden mitgerissen. Die Abflussspitze betrug 50 - 90 m³/s bei einem Ausbruchsvolumen von 300000 m³. Das gesamte Alpgebiet auf dem Kegel wurde übermurt, eine Alphütte zerstört. Die Simplonstrasse wurde beschädigt.','-','VAW 2003');
