INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('374de23c-0cde-4ffa-94ef-fe9072b11976','8049b51e-4ec8-11e8-84e7-985fd331b2ee','1970','07','08','','','1970','07','08','','','100000 - 150000','',null,null,'Ausbruch von See 3. Dabei flossen 170000 m³ Wasser ab und die Abflussmenge erreichte eine Spitze von etwa 15 m³ s-1. Dieser Wasserausbruch riss 100000 - 150000 m³ Material mit. Der Abfluss im Fallbach stieg exponentiell an und konnte gut prognostiziert werden. Da rechtzeitig alarmiert werden konnte, hatte dieses Ereignis geringe Schadenkonsequenzen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('7c7211eb-7496-40fc-82c0-6f84b21ec550','8049b51e-4ec8-11e8-84e7-985fd331b2ee','1971','09','22','','','1971','09','23','','','','',null,null,'Verstopfung des unterirdischen Auslaufs und Ausbruch von See 3. Der Durchbruch erfolgte ca. 2 m tiefer als die Sohle des Eisstollens. Bei diesem Ereignis wurde der Seespiegel um 2 m abgesenkt.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('9b4df562-7b85-4cb7-b49e-9bc9fcf3b936','8049b51e-4ec8-11e8-84e7-985fd331b2ee','1829','06','24','','','1829','06','25','','','','','1',null,'Ein Gletscherseesausbruch führte zu einem erhöhtem Abfluss und Geröllmitführung im Fallbach. Dieses Ereignis hatte ein Todesopfer und beträchtliche Schäden in Saas Balen zur Folge.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('aaed913f-f347-4576-8f49-01f3a813b012','8049b51e-4ec8-11e8-84e7-985fd331b2ee','1968','07','02','','','1968','07','02','','','400000','',null,null,'Ausbruch von See 3. Nachdem der unterirdische Ausfluss verstopft war (wahrscheinlich durch Absenkung des Gewölbedachs und Stauchung der Grundmoräne), stieg der Seespiegel um ca. 7 m an. In der Folge vermochte der zusatzliche Staudruck den Abflussweg freizulegen und es entleerten sich 170000 m³ Wasser. Dieser Ausbruch führte zum Überlaufen von See 2 und See 1. Dadurch wurde ein grosser Murgang ausgelöst, der ca. 400000 m³ Moränenmaterial mitschwemmte. In Saas Balen enstanden Sachschäden von rund 2 Mio CHF.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('c38d60d0-23b2-495b-a4bf-0baf9dbdd704','8049b51e-4ec8-11e8-84e7-985fd331b2ee','1958','08','09','','','1958','08','09','','','150000','',null,null,'Ausbruch von See 3, der 1954 durch den Gletscherrückzug entstand. Der randglaziale See mit einem Volumen von 150000 m³ entleerte sich nach einem Seespiegelanstieg oberflächlich durch einen 300-400 m langen, schmalen Überlauf entlang des kalten Eisdamms. Dieser Ausbruch war ohne Schadenfolge.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('ec98e4b6-cae7-47d4-bb27-d0bd8f0f4815','8049b51e-4ec8-11e8-84e7-985fd331b2ee','1868','06','20','','','1868','06','26','','','','',null,null,'Ein Gletschersee- oder Wassertaschenausbruch führte zu einem erhöhtem Abfluss und Geröllmitführung im Fallbach. Die Geröllablagerungen führten zu Schäden in Saas Balen und verlegten die Vispa.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('252b2c33-9c80-4bc1-be39-fabb9cc33abe','80b5bd5e-4ec8-11e8-88fd-985fd331b2ee','1921','10','03','15:00','','1921','10','03','','','300000','',null,null,'Ausbruch von 300000 m³ Wasser aus dem Gletschersee (2334 m ü. M). Das Hochwasser verwüstete die Aerlenalp und beschädigte die Grimselstrasse. Zwei Chalets und eine Brücke wurden mitgerissen.','-','VAW 2003');
