INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('df2eeaf6-ad28-41f7-8c0e-390f472bb96d','7ff7c48f-4ec8-11e8-9cfb-985fd331b2ee','1978','08','28','','','1978','08','28','','','80000 - 100000','',null,null,'Eine Wassertasche mit einem Ausbruchvolumen von ca. 80000 bis 100000 m³ und einer Abflussspitze grösser als 2 - 3 m³/s löste einen Murgang mit einem Schuttanteil von 80 % aus. Die Schuttablagerungen stauten die Vispa bis nach Täsch. Die Strasse und die BVZ Bahnlinie Täsch - Randa waren unterbrochen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('58f9a599-9c6b-465d-af2a-8eb45409124f','7f54cdcf-4ec8-11e8-bccc-985fd331b2ee','1977','07','','','','1977','07','','','','','',null,null,'Anfang Juli 1977 löste sich (vermutlich) ein kleiner Eissturz von einem Gletscher auf der Westseite des Berges Le Pleureur. Die Eismassen stürzten 1.5 km in die Tiefe und verschütteten die Kantonsstrasse in ca. 2.5 km Entfernung. Das Ereignis blieb ohne Schäden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('745a545d-2017-418c-9737-42559b7de606','800a140f-4ec8-11e8-be21-985fd331b2ee','1935','08','','','','1935','08','','','','','',null,null,'Ein grosser Eisabbruch erreichte die Lonza, ohne diese aufzustauen (vermutlich aus dem Sektor "Grosse Loibinbach").','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('f545647b-c5bb-465b-a6bc-261474108e3a','8002c10f-4ec8-11e8-a586-985fd331b2ee','1920','','','','','1920','','','','','','',null,null,'Ausbruch einer Wassertasche. Das Ereignis steht im Zusammenhang mit einem starken Vorstoss des Gletschers. An der Front war die Bewegung langsamer und Spannungsrisse führten zur Bildung und Entleerung der Wassertasche. Keine Schadenmeldung.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('492f58c6-10df-47f7-b95f-501eee194fd4','80a78c8f-4ec8-11e8-bc82-985fd331b2ee','1987','08','23','','','1987','08','23','','','','30000',null,null,'Auslösung eines grossen Murgangs aus einem Felscouloir im Vorfeld des Minstigergletschers. Unmittelbar nach diesem Ereignis wechselte der Abfluss die Gletscherseite. Die temporäre Verlegung des subglazialen Abflusssystems, möglicherweise kombiniert mit einer kurzfristigen Stauung und anschliessendem Wasserausbruch, löste diesen Murgang aus. Für den Ausbruch einer Wassertasche spricht der Umstand, dass ein erster Schwall des Hochwassers bereits eine Brücke wegriss. Erst später stieg der Wasserpegel als Folge intensiver Niederschläge zu kritischer Höhe an. Schliesslich führte der Bach übermässig Geschiebe und trat über die Ufer. Aufgrund des Gletscherrückzugs wurde die Anrissstelle für die Erosion freigelegt und damit das Murgangereignis ermöglicht. Der Murgang verklauste bei der Eisenbahnbrücke der Furka-Oberalp Bahnline (FO) das Gerinne und übersarte das Dorf Münster sowie eine Flache von ca. 30000 m².','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('85cbd020-f263-4a06-874f-91820e1e07e9','80a78c8f-4ec8-11e8-bc82-985fd331b2ee','1707','','','','','1707','','','','','','',null,null,'Ein Gletscherhochwasser verursachte Schäden im Dorf Münster.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('94cde5db-7fa9-4cb5-9248-1ad0079a8e41','80a78c8f-4ec8-11e8-bc82-985fd331b2ee','2008','08','21','','','2008','08','21','','','','',null,null,'Nach dem plötzlichen Ausbruch eines subglazialen Sees, der sich unmittelbar unter der Gletscheroberfläche am linken Rand befand, entstand ein Murgang, der das Retentionsbecken oberhab des Dorfes Münster füllte. Dieses konnte dem Druck des Wassers und Schutts nicht standhalten und Teile des Dorfs wurden überschwemmt. Grössere Schäden entstanden.','After the sudden release of a subglacial lake located immediately below the glacier surface at the left margin, a debris flow was formed which filled a safety reservoir upstream of the village of Münster. Unfortunately, the front part of the reservoir was unable to withstand the tremendous pressure from the water/debris and the floodwaters inundated parts of the village resulting in major damage.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('03e9309b-e02d-4075-973f-a819991aae4d','805d642e-4ec8-11e8-b774-985fd331b2ee','1975','09','11','','','1975','09','11','','','6000','',null,null,'Es ereigneten sich eine grössere Eislawine vom unteren Abbruch aus (‚Nase‘) und eine kleinere (6000 m³) vom oberen Abbruch aus (3900 m ü. M.)','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('33e62c86-bd32-41cf-b129-828e97cbb3f0','805d642e-4ec8-11e8-b774-985fd331b2ee','1984','07','05','03:00','','1984','07','05','','','340000','',null,null,'Die grösste bisher bekannte Eislawine vom Hängegletscher Mönch-Süd (340000 m³) legte eine totale horizontale Strecke von 690 m (Pauschalgefälle 34 %) auf dem Jungfraufirn zurück und verschüttete den Hüttenpfad zur Mönchsjochhütte.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('525826de-08a4-48ab-b9b8-855662cef0e0','805d642e-4ec8-11e8-b774-985fd331b2ee','1982','05','14','','','1982','05','14','','','55','',null,null,'Eisabbrüche vom Hängegletscher gefährdeten den Hüttenweg vom Jungfraujoch zur Mönchsjochhütte. Am 14. Mai 1982 glitt nach einem Absturz eine Eisscholle von ca. 55 m³ Volumen auf dem feuchten Firn über eine Distanz von 470 m (Pauschalgefälle 32 %) auf einer unberechenbaren Bahn.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('7245765f-269e-45df-9ebc-6c9947bc62ea','805d642e-4ec8-11e8-b774-985fd331b2ee','2009','05','24','13:00','','2009','05','24','','','50000 - 70000','',null,null,'Eine Eislamelle (50000 - 70000 m³) stürzte vom Hängegletscher an der Südflanke des Mönchs auf den Grossen Aletschgletscher. Ein Teil der Lawine erreichte die stark frequentierten touristischen Einrichtungen in der Nähe des Tunneleingangs zum Eispalast. Alle Touristen konnten jedoch rechtzeitig die Gefahrenzone verlassen.','An ice lamella (50000 - 70000 m³ of ice) broke off from the hanging glacier in the south face of Mönch on Grosser Aletschgletscher. A part of the avalanche reached the well attended touristic facilities close to the tunnel entrance in the Sphynx mountain. By chance all tourists could timely leave the endangered area.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('73c7b7a2-e5f8-405e-aa88-2cb17ad5bc47','805d642e-4ec8-11e8-b774-985fd331b2ee','1974','09','21','','','1974','09','21','','','','',null,null,'Eislawine von 3900 m ü. M. aus.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('bc496e45-d9cb-4049-9ac6-5cd59f3fbc5d','805d642e-4ec8-11e8-b774-985fd331b2ee','1970','09','21','','','1970','09','21','','','79000','',null,null,'Eislawine von 79000 m³,von der ‚Nase‘ auf etwa 3650 m ü. M. ausgehend.','-','UZH');
