INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('52a4f3c3-1b15-49c3-bcc5-f8daf3f4b555','7fed8b61-4ec8-11e8-9dea-985fd331b2ee','1982','02','20','','','1982','02','20','','','15000','',null,null,'Der Abbruch von 15000 m³ Eis an der rechten Zungenseite löste eine kombinierte Eis-Schneelawine, die beinahe die Lonza erreichte.','-','VAW 2003');
