INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('523dc106-effa-44f6-ae86-1f6c27fc006d','805267b0-4ec8-11e8-b67c-985fd331b2ee','1901','03','19','05:45','','1901','03','19','','','2500000 - 3000000','70000','2',null,'Die grösste bekannte kombinierte Fels-/Eis-Schneelawine der Alpen löste sich im März 1901 vom NW-Grat des Fletschhorns auf 3788 m ü. M. Das Ausbruchsvolumen betrug 2.5 bis 3 Mio m³, die Ablagerungen bedeckten eine Fläche von rund 70 ha, die Lawine überwand eine Höhendifferenz von 2300 m und hatte eine 7 km lange Auslaufsstrecke (Pauschalgefälle = 18% ). Bei diesem Ereignis verloren zwei Menschen, 13 Stück Gross- und 40 Stück Kleinvieh ihr Leben, 28 Gebäude wurden zerstört, Passstrasse und Telegrafenlinie wurden unterbrochen.','-','VAW 2003');
