INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('2d5804c4-70ed-4170-b965-3a6acd875479','805574f0-4ec8-11e8-9cd5-985fd331b2ee','1971','09','','','','1971','09','','','','','',null,null,'Hochwasser, das zur Verstopfung der Wasserfassung der Kraftwerke Mattmark führte. Während diesem Hochwasser wurde beinahe eine Brücke weggespült. Aufgrund der Schuttablagerungen musste das Bachbett korrigiert werden.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('459b49cb-9d5b-4255-8e87-689458180765','8035df00-4ec8-11e8-b025-985fd331b2ee','1922','08','02','','','1922','08','02','','','','',null,null,'Ein Eisabbruch reichte von der Gletscherzunge hinunter bis zur Lütschine, die auf einer Länge von 100 m überdeckt wurde.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('702084fd-6ddc-44ea-b0a6-c2f306bb0a43','8035df00-4ec8-11e8-b025-985fd331b2ee','2014','07','31','','','2014','07','31','','','1200000 - 1800000','',null,null,'Ausbruch eines englazialen Wasserreservoirs von 1.2 -1.8 Mio m³ Volumen. In der Weissen Lütschine wurden 10000 m³ Sediment abgelagert und mussten künstlich entfernt werden um Überschwemmungen vorzubeugen.','Outburst of an englacial water reservoir holding 1.2-1.8 million m³ of water. 10000 m³ of sediment were deposited in the Weisse Lütschine river and then artificially removed in order to prevent inundations.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('77cfb8ed-3b03-4e45-8fcd-c2cbea6951b3','805574f0-4ec8-11e8-9cd5-985fd331b2ee','1953','07','21','','','1953','07','21','','','10000','',null,null,'Ausbruch von 10000 m³ Wasser aus dem periglazialen See (2980 m ü. M.) am Fuss der Weissmies-Südwand. Zwischen diesem See und der Zunge des Rottalgletschers bildeten sich in dem schuttbedeckten Toteis verschiedene kleine Thermokarsttümpel. Das Auslaufen eines derartigen Tümpels liess den Pegel am unteren See um ca. 1 m ansteigen und führte schliesslich zu dessen Ausbruch. Das Wasser floss über eine steil gelagerte Blockgletscherstirn (30°), was zur raschen Rückwartserosion und Tieferlegung des Seeüberlaufs führte. Der dadurch ausgelöste Murgang bedeckte den flachen Boden der Almageller Alp mit Schutt. Die Schuttablagerungen erreichten im Tal die ersten Häuser von Saas Almagell. Es entstand ein beträchtlicher Flurschaden.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8d080007-0508-441c-a3c9-d98f8437f56d','8035df00-4ec8-11e8-b025-985fd331b2ee','1967','','','','','1967','','','','','','',null,null,'Wassertaschenausbruch ohne Schadenwirkung.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8435e370-2e72-4b0b-a31a-821031ce6f91','80fe380f-4ec8-11e8-9639-985fd331b2ee','1987','08','24','','','1987','08','24','','','210000','',null,null,'Murgang aus dem Vorfeld des Saasgletschers (Muttenhörner) im Zusammenhang mit den 1987er Unwettern. Der Murgang ereignete sich wahrscheinlich vor Mitternacht mit einem Anrisspunkt auf einer Höhe von 2520 m. Im Anrissgebiet findet man Grundmoränenmaterial und eventuell schwach ausgeprägte Stirnmoränen der Rückzugsstadien (Gletscherbedeckung von 1850 auf 2300 m) mit einer geschätzten Mächtigkeit von 15-25 m. Permafrost ist vorhanden und hielt ein beträchtliches Geschiebepotential zurück. Die Niederschlagssumme betrug 180 mm plus 30-35 mm aus der Schmelze. Die Hangneigung im Anrissgebiet beträgt 65 % (33°), die Fläche oberhalb des Anrisses 19 ha mit 10 ha Gletscheranteil. Der Anriss erfolgte vermutlich durch eine Instabilität auf 2440 m mit rückschreitender Erosion (Erosionsleistung 470 m³ im obersten Anrissgebiet), begrenzt durch den Fels (Basis und Seiten des Couloirs) und durch den Permafrost. Das Erosionvolumen betrug insgesamt 210000 m³, wovon 80’000 m³ auf das Anrissgebiet entfallen und 130000 m³ auf die Transitstrecke. Akkumuliert wurden total 190000 m³, das allermeiste auf dem Kegel (Höhe Kegelhals 1720 m). Das Pauschalgefälle wird mit 24% beziffert. Zu erwähnen gilt es die markante Erosionsleistung (ca. 50 m²) bei relativ gerinegem Gefälle (in angesprochenen Teilstück 18-20 %) sowie den minimalen Fliessquerschnitt (ca. 30 m²) bei sehr grosser Gesamtkubatur. Es entstanden Schäden durch Überführungen in Oberwald. Es ist jedoch schwer festzustellen, welche Schäden durch den Murgang aus dem Saastal und welche durch jene aus dem hinteren Gerental (ungefähr zeitgleiche Murgänge), verursacht wurden, da beide durch das Gerental abfliessen. ','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('1b8e9e69-f90c-4baa-b89d-9b0509db5edf','80200d0f-4ec8-11e8-84c6-985fd331b2ee','1820','','','','','1820','','','','','','',null,null,'1820 endet vorübergehend eine Vorstossphase des Gletschers, während der der Gletscher im Talgrund einen See aufstaute, der sich aber beim Rückzug des Eises langsam und Schaden entleerte. Seeablagerungen sind noch sichtbar.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a5aebf6d-0998-4d82-9dda-1d1811720aad','80eecec0-4ec8-11e8-bd44-985fd331b2ee','1987','08','24','','','1987','08','25','','','','',null,null,'Der proglaziale Moränensee entleerte sich während den Murgangereignissen im August 1987.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('ec39e7ac-8c04-4a1c-a210-ea082499df92','80317230-4ec8-11e8-9a41-985fd331b2ee','1982','','','','Sommer','1982','','','','','','',null,null,'Der Aufstieg von der Wengernalp zur Silberhornhütte war an mehreren Stellen durch Eisschlag gefährdet. Von diesem Aufstieg musste deshalb abgeraten werden. Eine eingehende Kontrolle des Gletschers über der Aufstiegsroute ergab, dass auch mit grösseren Abbrüchen gerechnet werden musste. Die Aufstiegsroute "Silberhornhütte - Silberhorn über N-Grat" war ebenfalls gefährdet.','-','VAW 2003');
