INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('323d9498-7239-45aa-8cd4-34c46bdeae96','7fbdefe1-4ec8-11e8-9a43-985fd331b2ee','1999','06','11','','','1999','06','11','','','100000','',null,null,'Ein grösserer Eisabbruch (100000 m³) verschüttete die Talstrasse und zerstörte eine Brücke über den Sillerebach, die Kander wurde gestaut und musste über den Sommerweg umgeleitet werden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('36b47f41-cc79-42f4-a088-90121fb5659f','7fbdefe1-4ec8-11e8-9a43-985fd331b2ee','1967','05','12','','','1967','05','12','','','','',null,'2','Eine Jeepkolonne der Armee, die zu einer Gefechtsübung unterwegs war, wurde von einer Eislawine überrascht. Dabei wurden 12 Soldaten vom Luftdruck erfasst und weggeschleudert, zwei wurden verletzt. Drei Militärfahrzeuge und ein PW sowie Waffen wurden verschüttet.','-','VAW 2003');
