INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('533e8fed-28bd-4094-bc8d-c55e88598477','80515640-4ec8-11e8-b4ef-985fd331b2ee','1699','','','','','1699','','','','','','',null,null,'Der Ausbruch eines Gletschersees oder einer Wassertasche im Triftgletschergebiet verursachte Flurschaden in Tamatten.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('ae9332e1-1b6b-4df8-abcc-640b397cc009','80515640-4ec8-11e8-b4ef-985fd331b2ee','2014','09','11','01:00','','2017','09','11','','','250000','',null,null,'250000 m³ der instabilen Eismasse am stürzten gegen 1 Uhr Morgens ab.','The remaining 250000 m³ of instable ice plummeted down on 11th September at 1:00 am.','Glaciological Report  No. 137/138');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('e732afb5-f909-4908-9899-6fd5f09e47e8','80515640-4ec8-11e8-b4ef-985fd331b2ee','2014','09','10','05:50','','2017','09','10','06:00','','500000','',null,null,'Kurz vor 6 Uhr morgens brach die instabile Zone des Triftgletschers im Umfang von 500000 m³ Eis ab. Zuvor ware eine Fliessgeschwindigkeit von 3m/d gemessen worden. Der Abbruch vollzog sich in Form mehrerer kleiner Eislawinen von weniger als 20000 m³  zwischen 5:50 und 6:00 Uhr. Da diese bereits im oberen Teil des Triftgletschers zum Stillstand kamen, entstanden keine Schäden.','Shortly before 6 am on the 10th September the unstable zone with 500000 m³ of ice flowed at a rate of over 3 m d-1 and broke off. Luckily, the ice avalanches came to a standstill in the upper portion of Triftgletscher without causing any damage for the sole reason that the immense volume of ice fell in the form of many small ice avalanches of less than 20000 m³ each between 5:50 and 6:00 am.','Glaciological Report  No. 137/138');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('97d8e0cf-e387-498d-94f9-75d64c1c53d7','7ff860cf-4ec8-11e8-bf2e-985fd331b2ee','1976','07','11','','','1976','07','11','','','','',null,null,'Eine Wassertasche entleerte sich mit flacher Abflussspitze (pulsierender Wasseranfall) und führte zur linienhaften Erosion im 30° steilen, schuttbedeckten Gletschervorfeld. Eisblöcke und Geschiebe verstopften das Durchlassrohr der Strasse zwischen Blatten und Gletscherstaffel und der Bach ergoss sich ins Dorf Blatten.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('199605d2-8e35-4166-9730-49c82e97f1f3','80644200-4ec8-11e8-aa15-985fd331b2ee','2008','05','30','','','2008','05','30','','','750000','',null,null,'Ein supraglazialer See mit einem Wasservolumen von 750000 m³ entleerte sich subglazial mit einem Spitzenabfluss von 100 m³/s in die Lütschine. Weideland und ein Golfplatz wurden überflutet. Das Flussbett füllte sich komplett mit Schutt und es entstand ein Schaden in Höhe von 400000 CHF an diversen Schutzbauten.','Subglacial drainage of a supraglacial lake occurred with a water volume of 750’000 m³ and a peak discharge of 100 m³/s in the Lütschine river. Pastureland as well as the golf course were flooded. The river channel was filled completely with debris, and damage totaling 400’000 Swiss francs (CHF) was caused to several protective structures.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('26a0e50f-a58f-4fad-88f5-eed3ba8a6ea5','80644200-4ec8-11e8-aa15-985fd331b2ee','1600','','','','','1600','','','','','','',null,null,'Durch den Vorstoss des Unteren Grindelwaldgletschers bis zum Burgbühl wurde die Lütschine umgeleitet und gestaut. Verschiedene Gebäude mussten auf die Felder der Aellawine evakuiert werden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('5107452c-a57f-4bbb-8a4d-f2a505ec67d2','80644200-4ec8-11e8-aa15-985fd331b2ee','1575','','','','','1602','','','','','','',null,null,'Vorstoss des Gletschers. Von 1575 bis 1602 verzeichnet der Untere Grindelwaldgletscher ein starkes Wachstum. Um 1600 reicht der Gletscher bis an den Burgbühl und den Schlüssellauinengraben. Die Lütschine wird vom Gletscher aufgestaut, verliert den rechten Lauf und läuft durch den Aellauinengraben aus. Ab 1602 bleibt der Gletscher stationär oder leicht zurückziehend (Periode 1602 bis 1620). Der Gletscher überrollt die Weiden, zerstört die Kapelle St. Petrus (?) (St. Pétronille) und Scheunen und Chalets und verfärbt die Lütschine schwarz.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('79025e15-61c4-4505-8c76-26bfcaf4eea1','80644200-4ec8-11e8-aa15-985fd331b2ee','1922','','','','','1922','','','','','','','1',null,'Der vorstossende Gletscher bildet eine senkrechte bis überhängende Front und gefährdet die Touristenpassage. Ein "Tunnelwächter" wird durch Eisschlag getötet, der Besuchersteg zur Eisgrotte langsam zerstört.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('9526c690-39a8-43a4-a02c-d54532852bba','80644200-4ec8-11e8-aa15-985fd331b2ee','1776','09','19','','','1776','09','20','','','','',null,null,'Ein Eisabbruch mit Steinen und Sand richtet zwischen den Lütschinen grosse Schäden an, Eisstücke bis Zweilütschinen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('9e2e1388-f564-420d-a513-a0dc854b28bd','80644200-4ec8-11e8-aa15-985fd331b2ee','1951','07','02','04:30','','1951','07','02','','','135000','',null,null,'Nach sub- oder intraglazialem Wasserstau brechen morgens ca. 135000 m³ Wasser aus. Die rund 5 m hohe Flutwelle richtete beträchtlichen Flurschaden an, riss zwei Brücken mit und setzte den Bahnhof Grund 50 cm tief unter Wasser. Dem Umstand, dass der Gletscherausbruch am frühen Morgen und nicht tagsüber erfolgte, ist es zu verdanken, dass keine Menschenleben zu beklagen waren.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('d9296c58-5ef8-4d1c-bce0-6667062ef904','80644200-4ec8-11e8-aa15-985fd331b2ee','1842','','','','Sommer','1842','','','','Sommer','','',null,null,'Ein Wasserausbruch (evtl. entstanden durch einen temporären Wasserrückstau innerhalb des Gletschers) richtete grosse Schäden an.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('967f3def-d2c2-43ca-a671-1f5521b155a2','81d229de-4ec8-11e8-91db-985fd331b2ee','1927','09','24','','','1927','09','24','','','2700000','',null,null,'Ein Wassertaschenausbruch verstärkte ein Niederschlagshochwasser. Das Abflussvolumen wurde auf 2.7 Mio m³ geschätzt. Bei diesem Ereignis wurde ein Grossteil des Kulturlandes und der Strassen zerstört sowie diverse Ortschaften bedroht.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('434ef93f-eb4b-4335-b3c2-3100a303a31b','82237e30-4ec8-11e8-a976-985fd331b2ee','2017','08','23','','','2017','08','23','','','4000000','','8',null,'An der Nordostflanke des Pizzo Cengalo (3369 m) ereignete sich ein Felssturz mit einem Volumen von über 4 Mio m³. Die darauffolgenden Murgänge wurden vermutlich durch Schmelzwasser ausgelöst, das sich durch den Aufprall der Felsmassen auf dem Vadret de la Bondasca und mehrjährigen Schneeflecken bildete.','A rockfall with a volume of about 4 million m³ occurred at the northeast face of Pizzo Cengalo (3369 m). Subsequent debris flows were assumably triggered by melt water, originating from the ice of Vadrec da la Bondasca and perennial snow patches in the area which was hit by the rockfall material. ','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('a3d06405-fc15-477e-b6a6-8fc8b9f53815','81ee8b80-4ec8-11e8-80d6-985fd331b2ee','2006','07','11','','','2006','07','11','','','','','1',null,'Nach dem plötzlichen Ausbruch einer subglazialen Wassertasche entstand im Gletschervorfeld ein Murgang aus Lockersedimenten. Der Murgang floss über einen Wanderweg und tötete eine Person.','After the sudden release of a subglacial water pocket, a debris flow was formed in the proglacial area covered with unconsolidated sediments. This debris flowed across a hiking trail and killed one person.','Glaciological Report  No. 135/136');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8a8efdde-7ca0-4fdd-ae0f-621c206e25f8','81fdcdc0-4ec8-11e8-a3b5-985fd331b2ee','1983','05','12','','','1983','05','12','','','','','3',null,'Eislawine. Am Morteratschgletscher ereignete sich die Eislawine zwischen der Bovalhütte und dem Rifugio Marco e Rosa, wahrscheinlich im Gebiet der grösseren Eisabbrüche auf der Route zum Piz Bernina. Drei Alpinisten wurden durch die Eislawine getötet.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('58fe7416-d439-495e-acef-bb5ee3e3e15a','81f430cf-4ec8-11e8-8fd6-985fd331b2ee','1983','05','12','','','1983','05','12','','','','','3',null,'Eisabbruch: 3 Bergsteiger im Aufstieg zum Piz Roseg wurden tödlich erschlagen.','-','WGMS');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('b709145b-2730-4b61-8d8d-43ae9375f22a','81f430cf-4ec8-11e8-8fd6-985fd331b2ee','1954','08','21','','','1954','08','22','','','120000','',null,null,'Im Verlauf des grossen Oberengadiner Hochwassers vom 21./22. August 1954 wurde infolge Schneeschmelze und starker Regenfälle der Seespiegel um 0.85 m angehoben. Durch das Einschneiden des Baches in die abdämmende Tschiervamoräne unter starker Erosion kam es zu einem raschen Abfliessen der aufgestauten Wassermassen. Dabei wurden etwa 30000 m³ Material talwärts geführt mit insgesamt 100 - 120000 m³ Wasserabfluss. Im Moränendamm wurde viel Toteis, unter dem Sander viel gefrorenes Seeis freigelegt. Durch die starke Erosion im Bachlauf wurde die Uferböschung (Schutthalde bei Muot da Crasta) destabilisiert, das Bachbett zwischen Muot da Crasta und Alp Prüna erhöht. Die ausgeflossenen Wasser- und Schuttmassen förderten gröberes Gerölle bis zur Alp Misau, das Feinmaterial (40-50%) bis in die Oberengadiner Talebene. Starke Schäden gab es im Rosegtal, wo Brücken fortgerissen, Weiden mit Geschiebe überführt, der Fahrweg und die Wasserleitung teilweise zerstört wurden und weiterer Schaden am Wals auftrat.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('c3a71248-0adb-46ec-8427-73c755904dcf','81f430cf-4ec8-11e8-8fd6-985fd331b2ee','1983','06','15','07:00','','1983','06','15','07:00','','','','3',null,'Eine Eislawine aus dem Hängegletscher in der Nordostwand des Piz Roseg tötete 3 Bergsteiger.','-','UZH');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('69041eea-d47a-4dba-ab3c-bed68ea7de81','81f1bfcf-4ec8-11e8-84aa-985fd331b2ee','1954','08','21','','','1954','08','22','','','','',null,null,'1944 waren die vormals vereinten Gletscherzungen des Vadret da Roseg und des Vadret da Tschierva getrennt. Hinter der orographisch linken Seitenmoräne des Vadret da Tschierva bildete sich darauf ein See. lm August 1954 wurde infolge Schneeschmelze und starker Regenfälle der Seespiegel gegenüber dem Stand des Vorjahres (Herbst) um 0.85 cm angehoben. Am Seeauslauf kam es zu starker Erosion im Bachbett, die aufgestauten Wassermengen flossen rasch ab, und der Seespiegel senkte sich wieder auf die ursprüngliche Kote. Die grossen Oberengadiner Schadenhochwasser vom August 1954 sind teilweise auf diesen Gletscherseeausbruch unterhalb der Gletscherzunge des Vadret da Roseg zurückzuführen.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('1e8adfc2-1b84-4af2-91d5-d7dbcec78115','7ffea261-4ec8-11e8-adef-985fd331b2ee','1868','','','','','1868','','','','','','',null,null,'Laut einer Notiz vom Pfarrer Taugwald soll sich 1868 ein zum Ereignis 1892 ähnliches Hochwasser ereignet haben, jedoch vom Täschgletscher ausgehend. Der Name dieser Gletscher wurde aber auf keinen Landkarten gefunden','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('266dbbdb-ed6e-418c-bcb9-838d2e557a38','7ffea261-4ec8-11e8-adef-985fd331b2ee','1985','','','','','1985','','','','','','',null,null,'Beim Vorstoss der nördlichsten Gletscherzunge wurde der Gletscherbach nach Norden verlagert, wo er gegen die nördliche Seitenmoräne gedrängt wurde. Dadurch entstand eine Murganggefahr.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('31e57bc2-8311-4eda-b90f-f13501fd1df7','7ffea261-4ec8-11e8-adef-985fd331b2ee','1980','08','','','','1980','08','','','','','',null,null,'lm warmen Sommer von 1980 floss viel Wasser in einen proglazialen See des südlichen Lappens des Weingartengletschers und der Seespiegel stieg stark an. lm August begann das Überlaufen des Sees, wobei sich ein Murgang bildete und eine Wasserfassung der Grande Dixence SA verstopfte. Die Strasse zur Täschalp musste zeitweilig gesperrt werden.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('6a92d3ce-d60c-4f54-9071-9ce47b9168bc','7ffea261-4ec8-11e8-adef-985fd331b2ee','1929','','','','','1929','','','','','','',null,null,'Hochwasserereignis in Täsch.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('6f2457c7-64b8-4dbb-979e-c02918cd32fc','7ffea261-4ec8-11e8-adef-985fd331b2ee','1948','','','','','1948','','','','','','',null,null,'Hochwasserereignis in Täsch.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('8c508965-bda0-4717-a911-aaf38ac665a9','7ff99951-4ec8-11e8-a29e-985fd331b2ee','1892','08','15','','','1892','08','17','','','','15000',null,null,'Bei einem grösseren Hochwasser sind beträchtliche Geschiebeablagerungen aufgetreten. Das Dorf Täsch sowie 15000 m² Wiesen wurden überschwemmt. Die Bahnlinie wurde nicht tangiert. Das Hochwasser wurde primär durch das orographisch rechte Seitengerinne des Rotbaches verursacht.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('9e4babd1-fb84-4904-9972-7a4297633119','7ff99951-4ec8-11e8-a29e-985fd331b2ee','2001','06','25','22:00','','2001','06','25','','','18000 - 20000','',null,null,'Während niederschlagsfreiem Wetter kam es zu einem Murgang, der starken Schaden in der Ortschaft Täsch anrichtete. Durch die starke Schneeschmelze kam es am linksseitigen Gerinne des Weingartensees zum Überlaufen. Weiter wurde das Moränenmaterial im Dammbereich einige Meter unterhalb des Überlaufs durch eine temporäre Quelle stark aufgelockert. Da sich die Rückhaltesperre östlich von Täsch als zu klein erwies, wurden etwa 18000- 20000 m³ (entspricht einem Geschiebeanteil von 10000- 12000 m³) in Täsch abgelagert.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('3f1e3fd9-db84-45be-a7e0-260ed0d61db3','7f9a3b40-4ec8-11e8-9c5e-985fd331b2ee','1850','','','','Um','1850','','','','Um','','',null,null,'Ein Gletschervorstoss zerstörte das Dorf Tiefenmatten.','-','VAW 2003');
INSERT INTO glacier_hazard.event (pk, fk_glacier, start_year, start_month, start_day, start_time, start_text, end_year, end_month, end_day, end_time, end_text, volume_m3, area_damaged_m2, dead_people, injured_people, description_de, description_en, secondary_source) VALUES ('71b43de7-cb6f-44fb-9e7b-c3bbd24fc6f9','7f9a3b40-4ec8-11e8-9c5e-985fd331b2ee','1899','10','','','','1899','10','','','','','',null,null,'Nach einem warmen Herbst brach eine grosse Wassertasche aus. Dieses Ereignis liess selbst in Sion den Wasserstandspegel der Rhone um 30 cm ansteigen.','-','VAW 2003');
