INSERT INTO glacier_hazard.hazard_type (pk, hazard_maintype_id, hazard_maintype_name, hazard_maintype_desc, hazard_subtype_id, hazard_subtype_name, hazard_subtype_desc) VALUES ('20','2','Glacier outburst flood','','0','unknown','');
INSERT INTO glacier_hazard.hazard_type (pk, hazard_maintype_id, hazard_maintype_name, hazard_maintype_desc, hazard_subtype_id, hazard_subtype_name, hazard_subtype_desc) VALUES ('21','2','Glacier outburst flood','','1','Internal glacier lake','Release of a water reservoir that has formed either on (supraglacial), within (englacial) or beneath (subglacial) a glacier (e.g. "water pockets"). ');
INSERT INTO glacier_hazard.hazard_type (pk, hazard_maintype_id, hazard_maintype_name, hazard_maintype_desc, hazard_subtype_id, hazard_subtype_name, hazard_subtype_desc) VALUES ('22','2','Glacier outburst flood','','2','Ice-dammed lake','Release of a water reservoir that has been dammed by glacier ice either in front (proglacial lake), at the glacier margin or where two glaciers merge. ');
INSERT INTO glacier_hazard.hazard_type (pk, hazard_maintype_id, hazard_maintype_name, hazard_maintype_desc, hazard_subtype_id, hazard_subtype_name, hazard_subtype_desc) VALUES ('23','2','Glacier outburst flood','','3','Moraine-dammed lake','Release of a water reservoir that has been dammed by a moraine of a receding glacier. Debris flows are often initiated by the drainage of such a lake.');
INSERT INTO glacier_hazard.hazard_type (pk, hazard_maintype_id, hazard_maintype_name, hazard_maintype_desc, hazard_subtype_id, hazard_subtype_name, hazard_subtype_desc) VALUES ('30','3','Glacier- or ice avalanche','','0','unknown','');
INSERT INTO glacier_hazard.hazard_type (pk, hazard_maintype_id, hazard_maintype_name, hazard_maintype_desc, hazard_subtype_id, hazard_subtype_name, hazard_subtype_desc) VALUES ('31','3','Glacier- or ice avalanche','','1','Ice avalanche','Ice avalanches form when a large mass of ice breaks off a) from the steep frontal section of a glacier (cliff situation), or b) from a sloping glacier bed (ramp or slab situation), drops down a slope driven by gravity and bursts into smaller pieces of ice.');
INSERT INTO glacier_hazard.hazard_type (pk, hazard_maintype_id, hazard_maintype_name, hazard_maintype_desc, hazard_subtype_id, hazard_subtype_name, hazard_subtype_desc) VALUES ('32','3','Glacier- or ice avalanche','','2','Icefall','Break-off of ice chunks at the glacier front (German: "Eisschlag").');
INSERT INTO glacier_hazard.hazard_type (pk, hazard_maintype_id, hazard_maintype_name, hazard_maintype_desc, hazard_subtype_id, hazard_subtype_name, hazard_subtype_desc) VALUES ('33','3','Glacier- or ice avalanche','','3','Combined ice and snow avalanche','Ice avalanche which can release or entrain additional snow masses. ');